import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext.js';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner.js';
import './Profile.css';

const Profile = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        // If we're using a mock user, just use the data from auth context
        if (!user.id.toString().includes('mock')) {
          const response = await axios.get(`http://localhost:5001/api/users/${user.id}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`
            }
          });
          setProfile(response.data);
        } else {
          // Use mock data from auth context
          setProfile({
            ...user,
            createdAt: new Date().toISOString(),
            username: user.name
          });
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError('Failed to fetch profile data. Please try again later.');
        setLoading(false);
      }
    };

    if (user) {
      fetchProfile();
    }
  }, [user]);

  if (loading) return <LoadingSpinner message="Loading profile data..." />;
  if (error) return <div className="error">{error}</div>;
  if (!profile) return <div className="error">Profile not found</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
      </div>
      <div className="profile-content">
        <div className="profile-info">
          <div className="info-group">
            <label>Username</label>
            <p>{profile.username || profile.name}</p>
          </div>
          <div className="info-group">
            <label>Email</label>
            <p>{profile.email}</p>
          </div>
          <div className="info-group">
            <label>Member Since</label>
            <p>{new Date(profile.createdAt).toLocaleDateString()}</p>
          </div>
        </div>
        <div className="profile-actions">
          <button className="edit-profile-btn">Edit Profile</button>
          <button className="change-password-btn">Change Password</button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
