import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [restaurantsRes, itemsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants'),
          axios.get('http://localhost:5000/api/menu/featured')
        ]);
        setRestaurants(restaurantsRes.data);
        setFeaturedItems(itemsRes.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch data');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="home">
      <section className="featured-items">
        <h2>Featured Items</h2>
        <div className="items-grid">
          {featuredItems.map(item => (
            <div key={item._id} className="food-item">
              <img 
                src={item.image || 'https://via.placeholder.com/300x200'} 
                alt={item.name}
                className="item-image"
              />
              <div className="item-info">
                <h3>{item.name}</h3>
                <p className="description">{item.description}</p>
                <div className="item-meta">
                  <span className="price">${item.price.toFixed(2)}</span>
                  <Link to={`/restaurants/${item.restaurant._id}`} className="restaurant-link">
                    {item.restaurant.name}
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      <section className="restaurants-section">
        <h2>Popular Restaurants</h2>
        <div className="restaurants-grid">
          {restaurants.map(restaurant => (
            <Link to={`/restaurants/${restaurant._id}`} key={restaurant._id} className="restaurant-card">
              <img 
                src={restaurant.image || 'https://via.placeholder.com/300x200'} 
                alt={restaurant.name}
                className="restaurant-image"
              />
              <div className="restaurant-info">
                <h3>{restaurant.name}</h3>
                <p className="cuisine">{restaurant.cuisine}</p>
                <div className="restaurant-meta">
                  <span>⭐ {restaurant.rating || '4.5'}</span>
                  <span>⏱️ {restaurant.deliveryTime || '30-45 min'}</span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
