import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        console.log('Fetching restaurants...');
        const response = await axios.get('http://localhost:5000/api/restaurants', {
          headers: {
            'Content-Type': 'application/json',
          }
        });
        console.log('Response received:', response.data);
        setRestaurants(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching restaurants:', err);
        setError(err.response?.data?.message || 'Failed to fetch restaurants. Please try again later.');
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, []);

  if (loading) return <div className="loading">Loading restaurants...</div>;
  if (error) return <div className="error">{error}</div>;
  if (restaurants.length === 0) return <div className="no-restaurants">No restaurants available at the moment.</div>;

  return (
    <div className="home">
      <h1 className="page-title">Restaurants</h1>
      <div className="restaurants-grid">
        {restaurants.map(restaurant => (
          <Link to={`/restaurants/${restaurant._id}`} key={restaurant._id} className="restaurant-card">
            <img 
              src={restaurant.image || 'https://via.placeholder.com/300x200'} 
              alt={restaurant.name}
              className="restaurant-image"
            />
            <div className="restaurant-info">
              <h3>{restaurant.name}</h3>
              <p className="cuisine">{restaurant.cuisine}</p>
              <div className="restaurant-meta">
                <span>⭐ {restaurant.rating || '4.5'}</span>
                <span>⏱️ {restaurant.deliveryTime || '30-45 min'}</span>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
