import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { FiClock, FiShoppingCart, FiStar, FiMapPin, FiSearch, FiPhone, FiCalendar } from 'react-icons/fi';
import Footer from '../components/Footer.js';
import './Home.css';

const Home = ({ dineOutMode }) => {
  const [restaurants, setRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categories] = useState(['All', 'Veg', 'Non-veg', 'Spicy', 'Healthy']);
  const { categoryId } = useParams();

  // Mock data for restaurants
  const mockRestaurants = [
    {
      id: 1,
      name: 'Tasty Bites',
      image: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8cmVzdGF1cmFudHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Indian',
      rating: 4.5,
      deliveryTime: '30-40 min',
      isOpen: true,
      discount: '20% OFF',
      location: 'Downtown',
      hasDineOut: false,
      address: '123 Main St, Downtown',
      phone: '(123) 456-7890'
    },
    {
      id: 2,
      name: 'Pizza Paradise',
      image: 'https://images.unsplash.com/photo-1555396273-367ea4eb4db5?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8cmVzdGF1cmFudHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Italian',
      rating: 4.8,
      deliveryTime: '25-35 min',
      isOpen: true,
      discount: '15% OFF',
      location: 'Midtown',
      hasDineOut: true,
      address: '456 Park Ave, Midtown',
      phone: '(123) 456-7891'
    },
    {
      id: 3,
      name: 'Sushi Master',
      image: 'https://images.unsplash.com/photo-1559339352-11d035aa65de?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTV8fHJlc3RhdXJhbnR8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Japanese',
      rating: 4.7,
      deliveryTime: '35-45 min',
      isOpen: false,
      location: 'Westside',
      hasDineOut: true,
      address: '789 Ocean Blvd, Westside',
      phone: '(123) 456-7892'
    },
    {
      id: 4,
      name: 'Le Bistro',
      image: 'https://images.unsplash.com/photo-1550966871-3ed3cdb5ed0c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80',
      cuisine: 'French',
      rating: 4.9,
      deliveryTime: '40-50 min',
      isOpen: true,
      location: 'French Quarter',
      hasDineOut: true,
      address: '101 River St, French Quarter',
      phone: '(123) 456-7893'
    },
    {
      id: 5,
      name: 'Taco Town',
      image: 'https://images.unsplash.com/photo-1552566626-52f8b828add9?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80',
      cuisine: 'Mexican',
      rating: 4.3,
      deliveryTime: '20-30 min',
      isOpen: true,
      discount: '10% OFF',
      location: 'Eastside',
      hasDineOut: false,
      address: '222 Sunset Rd, Eastside',
      phone: '(123) 456-7894'
    }
  ];

  // Mock data for featured items
  const mockFeaturedItems = [
    {
      id: 1,
      name: 'Butter Chicken',
      description: 'Tender chicken in rich tomato gravy with a blend of aromatic spices and fresh cream',
      price: 12.99,
      image: 'https://images.unsplash.com/photo-1588166524941-3bf61a9c41db?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=684&q=80',
      restaurant: 'Tasty Bites',
      restaurantId: 1,
      rating: 4.5,
      tags: ['non-veg', 'spicy'],
      discount: '10% OFF'
    },
    {
      id: 2,
      name: 'Margherita Pizza',
      description: 'Classic pizza with fresh basil, mozzarella, and tomato sauce on a thin, crispy crust',
      price: 10.99,
      image: 'https://images.unsplash.com/photo-1574071318508-1cdbab80d002?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1169&q=80',
      restaurant: 'Pizza Paradise',
      restaurantId: 2,
      rating: 4.8,
      tags: ['veg'],
      reviewCount: 127
    },
    {
      id: 3,
      name: 'California Roll',
      description: 'Fresh sushi with crab, avocado, cucumber, and tobiko wrapped in seaweed and rice',
      price: 14.99,
      image: 'https://images.unsplash.com/photo-1579871494447-9811cf80d66c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Sushi Master',
      restaurantId: 3,
      rating: 4.7,
      tags: ['non-veg'],
      reviewCount: 84
    }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Filter restaurants based on dineOutMode
        let filteredRestaurants = [...mockRestaurants];

        if (dineOutMode) {
          filteredRestaurants = mockRestaurants.filter(restaurant => restaurant.hasDineOut);
        }

        // Filter by category if specified
        if (categoryId) {
          filteredRestaurants = filteredRestaurants.filter(restaurant => 
            restaurant.cuisine.toLowerCase() === categoryId.toLowerCase()
          );
        }

        setRestaurants(filteredRestaurants);
        setFeaturedItems(mockFeaturedItems);
        setError(null);
        setLoading(false);

        // Uncomment this section when your backend is ready
        /*
        const endpoint = dineOutMode ? '/api/restaurants/dine-out' : '/api/restaurants';
        const [restaurantsRes, featuredItemsRes] = await Promise.all([
          axios.get(`http://localhost:5001${endpoint}`),
          axios.get('http://localhost:5001/api/menu/featured')
        ]);

        setRestaurants(restaurantsRes.data);
        setFeaturedItems(featuredItemsRes.data);
        setError(null);
        */
      } catch (err) {
        console.error('Error fetching data:', err);
        // Use mock data as fallback
        setRestaurants(dineOutMode ? 
          mockRestaurants.filter(r => r.hasDineOut) : 
          mockRestaurants
        );
        setFeaturedItems(mockFeaturedItems);
        setError('Using sample data for demonstration');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [dineOutMode, categoryId]);

  const handleSearch = (e) => {
    e.preventDefault();
    const filteredRestaurants = mockRestaurants.filter(restaurant =>
      restaurant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      restaurant.cuisine.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setRestaurants(filteredRestaurants);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    if (category) {
      const filteredItems = mockFeaturedItems.filter(item =>
        item.tags.includes(category.toLowerCase())
      );
      setFeaturedItems(filteredItems);
    } else {
      setFeaturedItems(mockFeaturedItems);
    }
  };

  const handleAddToCart = (item) => {
    // Save item to local storage cart
    const cart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = cart.find(cartItem => cartItem.id === item.id);

    if (existingItem) {
      existingItem.quantity = (existingItem.quantity || 1) + 1;
      localStorage.setItem('cart', JSON.stringify(cart));
    } else {
      cart.push({ ...item, quantity: 1 });
      localStorage.setItem('cart', JSON.stringify(cart));
    }

    alert('Item added to cart!');
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className={`hero-section ${dineOutMode ? 'dine-out' : ''}`}>
        <div className="hero-content">
          <h1>{dineOutMode ? 'Find the Perfect Restaurant to Dine Out' : 'Delicious Food Delivered To Your Doorstep'}</h1>
          <p>{dineOutMode ? 'Discover top-rated restaurants for your dining experience' : 'Order from your favorite restaurants with just a few clicks'}</p>
          <form className="search-bar" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder={dineOutMode ? "Search for dine-in restaurants..." : "Search for restaurants or cuisine..."}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">
              <FiSearch className="search-icon" /> Search
            </button>
          </form>
        </div>
      </section>

      {/* Categories */}
      <section className="categories-section">
        <div className="container">
          <div className="category-tabs">
            {categories.map((category, index) => (
              <button
                key={index}
                className={`category-tab ${selectedCategory === category.toLowerCase() ? 'active' : ''}`}
                onClick={() => handleCategorySelect(category === 'All' ? null : category.toLowerCase())}
              >
                {category}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Items Section - Hide in Dine Out mode */}
      {!dineOutMode && (
        <section className="featured-section">
          <div className="section-header">
            <h2 className="section-title">Featured Items</h2>
            <Link to="/menu" className="view-all">View All</Link>
          </div>

          {loading ? (
            <div className="loading-spinner">
              <div className="spinner"></div>
            </div>
          ) : error ? (
            <div className="error-message">
              <p>{error}</p>
              <button onClick={() => window.location.reload()}>Try Again</button>
            </div>
          ) : (
            <div className="food-items-grid">
              {featuredItems.map((item) => (
                <div key={item.id} className="food-item-card">
                  {item.discount && (
                    <div className="discount-badge">{item.discount}</div>
                  )}
                  <div className="food-image-container">
                    <img src={item.image} alt={item.name} className="food-item-image" />
                  </div>
                  <div className="food-item-info">
                    <div className="food-tags">
                      {item.tags.map((tag, index) => (
                        <span key={index} className={`tag ${tag}`}>{tag}</span>
                      ))}
                    </div>
                    <h3>{item.name}</h3>
                    <p className="food-description">{item.description}</p>
                    <div className="food-item-meta">
                      <div className="rating">
                        <span><FiStar /> {item.rating} {item.reviewCount && <small>({item.reviewCount})</small>}</span>
                        <span className="restaurant-link">
                          <Link to={`/restaurant/${item.restaurantId}`}>
                            {item.restaurant}
                          </Link>
                        </span>
                      </div>
                      <span className="food-price">${item.price.toFixed(2)}</span>
                    </div>
                    <button
                      className="add-to-cart-btn"
                      onClick={() => handleAddToCart(item)}
                    >
                      <FiShoppingCart /> Add to Cart
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      )}

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">{dineOutMode ? 'Dine Out Restaurants' : 'Popular Restaurants'}</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
        ) : error ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => window.location.reload()}>Try Again</button>
          </div>
        ) : (
          <div className="restaurants-grid">
            {restaurants.map((restaurant) => (
              <Link
                key={restaurant.id}
                to={`/restaurant/${restaurant.id}`}
                className={`restaurant-card ${dineOutMode ? 'dine-out-card' : ''}`}
              >
                <div className="restaurant-image-container">
                  <img
                    src={restaurant.image}
                    alt={restaurant.name}
                    className="restaurant-image"
                  />
                  {restaurant.discount && (
                    <div className="discount-badge">{restaurant.discount}</div>
                  )}
                  {dineOutMode && restaurant.hasDineOut && (
                    <div className="dine-out-badge">Dine-In Available</div>
                  )}
                </div>
                <div className="restaurant-info">
                  <h3>{restaurant.name}</h3>
                  <p className="cuisine">{restaurant.cuisine}</p>

                  <div className="restaurant-meta">
                    <span className="rating"><FiStar /> {restaurant.rating}</span>
                    {!dineOutMode ? (
                      <span className="delivery-info">
                        <FiClock /> {restaurant.deliveryTime}
                      </span>
                    ) : (
                      <span className="phone-info">
                        <FiPhone /> {restaurant.phone}
                      </span>
                    )}
                    <span className="location">
                      <FiMapPin /> {restaurant.location}
                    </span>
                  </div>

                  {dineOutMode && (
                    <div className="reservation-info">
                      <button className="reserve-btn">
                        <FiCalendar /> Reserve a Table
                      </button>
                    </div>
                  )}

                  <div className="restaurant-status">
                    <span className={`status ${restaurant.isOpen ? 'open' : 'closed'}`}>
                      {restaurant.isOpen ? 'Open Now' : 'Closed'}
                    </span>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* Call to Action Section */}
      <section className="cta-section">
        <div className="cta-content">
          <h2>Download Our App</h2>
          <p>Get exclusive offers and {dineOutMode ? 'reserve tables' : 'order'} on the go with our mobile app</p>
          <div className="app-buttons">
            <a href="#" className="app-button">
              <span className="app-store-icon">App Store</span>
            </a>
            <a href="#" className="app-button">
              <span className="play-store-icon">Google Play</span>
            </a>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
