import React, { useState, useEffect } from 'react';
import { FiShoppingCart, FiFilter, FiSearch, FiX, FiChevronDown } from 'react-icons/fi';
import { useCart } from '../context/CartContext.js';
import './OrderOnline.css';

const OrderOnline = () => {
  const [foodItems, setFoodItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeCategory, setActiveCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState('');
  const [notification, setNotification] = useState({ show: false, message: '', type: '' });
  const [activeFilters, setActiveFilters] = useState([]);
  const [sortBy, setSortBy] = useState('');
  const [showSortOptions, setShowSortOptions] = useState(false);
  const { addToCart } = useCart();

  // Categories and filter options
  const categories = ['All', 'Appetizers', 'Main Course', 'Desserts', 'Beverages'];

  const filterOptions = [
    { id: 'offers', label: 'Offers', removable: true },
    { id: 'fastDelivery', label: 'Fast Delivery' },
    { id: 'new', label: 'New on Swiggy' },
    { id: 'ratings', label: 'Ratings 4.0+' },
    { id: 'veg', label: 'Pure Veg' },
    { id: 'price300-600', label: 'Rs. 300-Rs. 600' },
    { id: 'priceLess300', label: 'Less than Rs. 300' }
  ];

  const sortOptions = [
    { id: 'relevance', label: 'Relevance (Default)' },
    { id: 'delivery', label: 'Delivery Time' },
    { id: 'rating', label: 'Rating' },
    { id: 'costLowToHigh', label: 'Cost: Low to High' },
    { id: 'costHighToLow', label: 'Cost: High to Low' }
  ];

  const cuisines = ['Gujarati', 'South Indian', 'Punjabi', 'Chinese', 'Italian', 'North Indian', 'Fast Food', 'Street Food'];

  useEffect(() => {
    // In a real app, this would fetch from your API
    const fetchFoodItems = async () => {
      try {
        // Simulating API call with sample data
        const sampleRestaurants = [
          {
            _id: "r1",
            name: "Jani Locho",
            cuisines: ["Gujarati", "Snacks"],
            rating: 4.5,
            deliveryTime: "20-25 mins",
            priceRange: "₹100 for two",
            location: "Adajan Patiya",
            offer: "50% OFF UPTO ₹100",
            image: "https://images.unsplash.com/photo-1606491956689-2ea866880c84?w=600&auto=format",
            isVeg: true,
            isNew: false
          },
          {
            _id: "r2",
            name: "Jalaram Locho & Khaman",
            cuisines: ["Gujarati", "Snacks", "Fast Food", "Street Food"],
            rating: 4.4,
            deliveryTime: "20-25 mins",
            priceRange: "₹150 for two",
            location: "Gotalawadi, Katargam",
            offer: "₹125 OFF ABOVE ₹249",
            image: "https://images.unsplash.com/photo-1631452180519-c014fe946bc7?w=600&auto=format",
            isVeg: true,
            isNew: false
          },
          {
            _id: "r3",
            name: "Shree Tirupati Balaji Idli",
            cuisines: ["South Indian"],
            rating: 4.5,
            deliveryTime: "30-35 mins",
            priceRange: "₹200 for two",
            location: "Athwa",
            offer: "₹125 OFF ABOVE ₹349",
            image: "https://images.unsplash.com/photo-1626132647523-66f5bf380002?w=600&auto=format",
            isVeg: true,
            isNew: false
          },
          {
            _id: "r4",
            name: "G Dada",
            cuisines: ["Gujarati", "Snacks", "Street Food", "Punjabi"],
            rating: 4.4,
            deliveryTime: "20-25 mins",
            priceRange: "₹135 for two",
            location: "Adajan Gam",
            offer: "ITEMS AT ₹135",
            image: "https://images.unsplash.com/photo-1645112411341-6c4fd023a3f1?w=600&auto=format",
            isVeg: true,
            isNew: true
          },
          {
            _id: "r5",
            name: "Punjab Grill",
            cuisines: ["North Indian", "Punjabi", "Curry"],
            rating: 4.3,
            deliveryTime: "35-40 mins",
            priceRange: "₹350 for two",
            location: "City Light",
            offer: "20% OFF UPTO ₹120",
            image: "https://images.unsplash.com/photo-1585937421612-70a008356c36?w=600&auto=format",
            isVeg: false,
            isNew: false
          },
          {
            _id: "r6",
            name: "La Pino'z Pizza",
            cuisines: ["Italian", "Pizza", "Fast Food"],
            rating: 4.2,
            deliveryTime: "25-30 mins",
            priceRange: "₹400 for two",
            location: "Vesu",
            offer: "₹150 OFF ABOVE ₹499",
            image: "https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?w=600&auto=format",
            isVeg: false,
            isNew: false
          },
          {
            _id: "r7",
            name: "Bikanervala",
            cuisines: ["North Indian", "Sweets", "Snacks"],
            rating: 4.6,
            deliveryTime: "15-20 mins",
            priceRange: "₹250 for two",
            location: "Piplod",
            offer: "10% OFF UPTO ₹150",
            image: "https://images.unsplash.com/photo-1589301760014-d929f3979dbc?w=600&auto=format",
            isVeg: true,
            isNew: false
          },
          {
            _id: "r8",
            name: "Chinese Wok",
            cuisines: ["Chinese", "Thai", "Asian"],
            rating: 4.1,
            deliveryTime: "30-35 mins",
            priceRange: "₹300 for two",
            location: "Althan",
            offer: "FREE DELIVERY",
            image: "https://images.unsplash.com/photo-1563245372-f21724e3856d?w=600&auto=format",
            isVeg: false,
            isNew: true
          }
        ];

        setFoodItems(sampleRestaurants);
        setFilteredItems(sampleRestaurants);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch restaurants');
        setLoading(false);
      }
    };

    fetchFoodItems();
  }, []);

  useEffect(() => {
    // Filter items based on category, search term, and active filters
    const filterItems = () => {
      let filtered = [...foodItems];

      // Filter by search term
      if (searchTerm) {
        filtered = filtered.filter(item => 
          item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          item.cuisines.some(cuisine => cuisine.toLowerCase().includes(searchTerm.toLowerCase())) ||
          item.location.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      // Apply active filters
      if (activeFilters.includes('offers')) {
        filtered = filtered.filter(item => item.offer);
      }

      if (activeFilters.includes('fastDelivery')) {
        filtered = filtered.filter(item => {
          const maxTime = parseInt(item.deliveryTime.split('-')[1]);
          return maxTime <= 30;
        });
      }

      if (activeFilters.includes('new')) {
        filtered = filtered.filter(item => item.isNew);
      }

      if (activeFilters.includes('ratings')) {
        filtered = filtered.filter(item => item.rating >= 4.0);
      }

      if (activeFilters.includes('veg')) {
        filtered = filtered.filter(item => item.isVeg);
      }

      if (activeFilters.includes('price300-600')) {
        filtered = filtered.filter(item => {
          const price = parseInt(item.priceRange.split(' ')[0].substring(1));
          return price >= 300 && price <= 600;
        });
      }

      if (activeFilters.includes('priceLess300')) {
        filtered = filtered.filter(item => {
          const price = parseInt(item.priceRange.split(' ')[0].substring(1));
          return price < 300;
        });
      }

      // Sort items
      if (sortBy) {
        switch(sortBy) {
          case 'delivery':
            filtered.sort((a, b) => {
              const aTime = parseInt(a.deliveryTime.split('-')[0]);
              const bTime = parseInt(b.deliveryTime.split('-')[0]);
              return aTime - bTime;
            });
            break;
          case 'rating':
            filtered.sort((a, b) => b.rating - a.rating);
            break;
          case 'costLowToHigh':
            filtered.sort((a, b) => {
              const aPrice = parseInt(a.priceRange.split(' ')[0].substring(1));
              const bPrice = parseInt(b.priceRange.split(' ')[0].substring(1));
              return aPrice - bPrice;
            });
            break;
          case 'costHighToLow':
            filtered.sort((a, b) => {
              const aPrice = parseInt(a.priceRange.split(' ')[0].substring(1));
              const bPrice = parseInt(b.priceRange.split(' ')[0].substring(1));
              return bPrice - aPrice;
            });
            break;
          default:
            // Default is relevance, no specific sorting
            break;
        }
      }

      setFilteredItems(filtered);
    };

    filterItems();
  }, [activeFilters, sortBy, searchTerm, foodItems]);

  const toggleFilter = (filterId) => {
    setActiveFilters(prev => {
      if (prev.includes(filterId)) {
        return prev.filter(id => id !== filterId);
      } else {
        return [...prev, filterId];
      }
    });
  };

  const handleSortSelect = (sortId) => {
    setSortBy(sortId);
    setShowSortOptions(false);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleAddToCart = (item) => {
    // Create a mock menu item from restaurant for cart
    const menuItem = {
      _id: `menu-${item._id}`,
      name: `${item.name} Special`,
      price: parseInt(item.priceRange.split(' ')[0].substring(1)),
      image: item.image,
      restaurant: item.name,
      restaurantId: item._id
    };

    addToCart(menuItem);
    setNotification({
      show: true,
      message: `${menuItem.name} added to cart!`,
      type: 'success'
    });

    // Hide notification after 3 seconds
    setTimeout(() => {
      setNotification({ show: false, message: '', type: '' });
    }, 3000);
  };

  if (loading) {
    return <div className="loading">Loading restaurants...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="order-online-container">
      {notification.show && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      <div className="page-tabs">
        <button className="tab active">Order Online</button>
        <button className="tab">Dineout</button>
      </div>

      <div className="search-filter-container">
        <div className="search-bar">
          <FiSearch className="search-icon" />
          <input 
            type="text"
            placeholder="Search for restaurants and food..." 
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
      </div>

      <div className="filter-options">
        <div className="filter-pill main-filter">
          <FiFilter />
          <span>Filter</span>
          <span className="filter-count">{activeFilters.length || ''}</span>
        </div>

        <div className="filter-pill sort-by" onClick={() => setShowSortOptions(!showSortOptions)}>
          <span>Sort By</span>
          <FiChevronDown />
          {showSortOptions && (
            <div className="sort-options">
              {sortOptions.map(option => (
                <div 
                  key={option.id} 
                  className={`sort-option ${sortBy === option.id ? 'active' : ''}`}
                  onClick={() => handleSortSelect(option.id)}
                >
                  {option.label}
                </div>
              ))}
            </div>
          )}
        </div>

        {filterOptions.map(filter => (
          <div 
            key={filter.id}
            className={`filter-pill ${activeFilters.includes(filter.id) ? 'active' : ''}`}
            onClick={() => toggleFilter(filter.id)}
          >
            {filter.label}
            {filter.removable && activeFilters.includes(filter.id) && (
              <FiX className="remove-filter" />
            )}
          </div>
        ))}
      </div>

      <div className="restaurants-grid">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <div key={item._id} className="restaurant-card">
              <div className="restaurant-image">
                <img src={item.image} alt={item.name} />
                <div className="offer-badge">{item.offer}</div>
              </div>
              <div className="restaurant-info">
                <h3>{item.name}</h3>
                <div className="rating-delivery">
                  <span className="rating">
                    <span className="star">★</span> {item.rating}
                  </span>
                  <span className="dot">•</span>
                  <span className="delivery-time">{item.deliveryTime}</span>
                </div>
                <p className="cuisines">{item.cuisines.join(', ')}</p>
                <p className="location">{item.location}</p>
                <button className="add-to-cart-btn" onClick={() => handleAddToCart(item)}>
                  <FiShoppingCart /> Order Now
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-results">
            <h3>No restaurants found</h3>
            <p>Try changing your filters or search term</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderOnline;
