import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Footer from '../components/Footer.js';
import './Home.css';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Mock data for restaurants
  const mockRestaurants = [
    {
      id: 1,
      name: 'Tasty Bites',
      image: '/images/restaurants/restaurant1.jpg',
      cuisine: 'Indian',
      rating: 4.5,
      deliveryTime: '30-40 min',
      isOpen: true,
      discount: '20% OFF'
    },
    {
      id: 2,
      name: 'Pizza Paradise',
      image: '/images/restaurants/restaurant2.jpg',
      cuisine: 'Italian',
      rating: 4.8,
      deliveryTime: '25-35 min',
      isOpen: true,
      discount: '15% OFF'
    },
    {
      id: 3,
      name: 'Sushi Master',
      image: '/images/restaurants/restaurant3.jpg',
      cuisine: 'Japanese',
      rating: 4.7,
      deliveryTime: '35-45 min',
      isOpen: false
    }
  ];

  // Mock data for featured items
  const mockFeaturedItems = [
    {
      id: 1,
      name: 'Butter Chicken',
      description: 'Tender chicken in rich tomato gravy',
      price: 12.99,
      image: '/images/food/butter-chicken.jpg',
      restaurant: 'Tasty Bites',
      restaurantId: 1,
      rating: 4.5,
      tags: ['non-veg', 'spicy'],
      discount: '10% OFF'
    },
    {
      id: 2,
      name: 'Margherita Pizza',
      description: 'Classic pizza with fresh basil',
      price: 10.99,
      image: '/images/food/pizza.jpg',
      restaurant: 'Pizza Paradise',
      restaurantId: 2,
      rating: 4.8,
      tags: ['veg']
    },
    {
      id: 3,
      name: 'California Roll',
      description: 'Fresh sushi with crab and avocado',
      price: 14.99,
      image: '/images/food/sushi.jpg',
      restaurant: 'Sushi Master',
      restaurantId: 3,
      rating: 4.7,
      tags: ['non-veg']
    }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Use mock data directly instead of making API calls
        setRestaurants(mockRestaurants);
        setFeaturedItems(mockFeaturedItems);
        setError(null);
        setLoading(false);

        // Uncomment this section when your backend is ready
        /*
        const [restaurantsRes, featuredItemsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants'),
          axios.get('http://localhost:5000/api/menu/featured')
        ]);

        setRestaurants(restaurantsRes.data);
        setFeaturedItems(featuredItemsRes.data);
        setError(null);
        */
      } catch (err) {
        console.error('Error fetching data:', err);
        // Use mock data as fallback
        setRestaurants(mockRestaurants);
        setFeaturedItems(mockFeaturedItems);
        setError('Using sample data for demonstration');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    const filteredRestaurants = mockRestaurants.filter(restaurant =>
      restaurant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      restaurant.cuisine.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setRestaurants(filteredRestaurants);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    if (category) {
      const filteredItems = mockFeaturedItems.filter(item =>
        item.tags.includes(category.toLowerCase())
      );
      setFeaturedItems(filteredItems);
    } else {
      setFeaturedItems(mockFeaturedItems);
    }
  };

  const handleAddToCart = (item) => {
    // Save item to local storage cart
    const cart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = cart.find(cartItem => cartItem.id === item.id);

    if (existingItem) {
      existingItem.quantity = (existingItem.quantity || 1) + 1;
      localStorage.setItem('cart', JSON.stringify(cart));
    } else {
      cart.push({ ...item, quantity: 1 });
      localStorage.setItem('cart', JSON.stringify(cart));
    }

    alert('Item added to cart!');
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Delicious Food Delivered To Your Doorstep</h1>
          <p>Order from your favorite restaurants with just a few clicks</p>
          <form className="search-bar" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for restaurants or food..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>
      </section>

      {/* Featured Items Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2 className="section-title">Featured Items</h2>
          <Link to="/menu" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading">Loading...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : (
          <div className="food-items-grid">
            {featuredItems.map((item) => (
              <div key={item.id} className="food-item-card">
                {item.discount && (
                  <div className="discount-badge">{item.discount}</div>
                )}
                <img src={item.image} alt={item.name} className="food-item-image" />
                <div className="food-item-info">
                  <div className="food-tags">
                    {item.tags.map((tag, index) => (
                      <span key={index} className={`tag ${tag}`}>{tag}</span>
                    ))}
                  </div>
                  <h3>{item.name}</h3>
                  <p className="food-description">{item.description}</p>
                  <div className="food-item-meta">
                    <div className="rating">
                      <span>⭐ {item.rating}</span>
                      <span className="restaurant-link">
                        <Link to={`/restaurant/${item.restaurantId}`}>
                          {item.restaurant}
                        </Link>
                      </span>
                    </div>
                    <span className="food-price">${item.price}</span>
                  </div>
                  <button
                    className="add-to-cart-btn"
                    onClick={() => handleAddToCart(item)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">Popular Restaurants</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading">Loading...</div>
        ) : error ? (
          <div className="error">{error}</div>
        ) : (
          <div className="restaurants-grid">
            {restaurants.map((restaurant) => (
              <Link
                key={restaurant.id}
                to={`/restaurant/${restaurant.id}`}
                className="restaurant-card"
              >
                <img
                  src={restaurant.image}
                  alt={restaurant.name}
                  className="restaurant-image"
                />
                <div className="restaurant-info">
                  <h3>{restaurant.name}</h3>
                  <p className="cuisine">{restaurant.cuisine}</p>
                  <div className="restaurant-meta">
                    <div className="delivery-info">
                      <span>⭐ {restaurant.rating}</span>
                      <span>⏱️ {restaurant.deliveryTime}</span>
                    </div>
                    <div className="restaurant-status">
                      <span className={`status ${restaurant.isOpen ? 'open' : 'closed'}`}>
                        {restaurant.isOpen ? 'Open' : 'Closed'}
                      </span>
                    </div>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* App Download Section */}
      <section className="app-download-section">
        <div className="app-content">
          <div className="app-info">
            <h2>Download Our App</h2>
            <p>Get exclusive offers and faster delivery with our mobile app</p>
            <div className="app-features">
              <div className="feature">
                <i className="fas fa-bolt feature-icon"></i>
                <span>Fast Delivery</span>
              </div>
              <div className="feature">
                <i className="fas fa-percent feature-icon"></i>
                <span>Exclusive Offers</span>
              </div>
              <div className="feature">
                <i className="fas fa-bell feature-icon"></i>
                <span>Order Tracking</span>
              </div>
            </div>
            <div className="app-buttons">
              <button className="app-store-btn">
                <span>Download on the</span>
                <span>App Store</span>
              </button>
              <button className="play-store-btn">
                <span>Get it on</span>
                <span>Google Play</span>
              </button>
            </div>
          </div>
          <div className="app-image">
            <img src="https://via.placeholder.com/300x600" alt="App Preview" />
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
