import React, { createContext, useContext, useState } from 'react';

const SearchContext = createContext();

export const SearchProvider = ({ children }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState({
    restaurants: [],
    foodItems: [],
  });
  const [isSearching, setIsSearching] = useState(false);

  const search = async (query) => {
    if (!query.trim()) {
      setSearchResults({ restaurants: [], foodItems: [] });
      return;
    }

    setIsSearching(true);
    try {
      const [restaurantsRes, foodItemsRes] = await Promise.all([
        fetch(`http://localhost:5000/api/restaurants/search?q=${encodeURIComponent(query)}`),
        fetch(`http://localhost:5000/api/menu/search?q=${encodeURIComponent(query)}`),
      ]);

      const restaurants = await restaurantsRes.json();
      const foodItems = await foodItemsRes.json();

      setSearchResults({
        restaurants,
        foodItems,
      });
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults({
        restaurants: [],
        foodItems: [],
      });
    } finally {
      setIsSearching(false);
    }
  };

  return (
    <SearchContext.Provider
      value={{
        searchQuery,
        setSearchQuery,
        searchResults,
        isSearching,
        search,
      }}
    >
      {children}
    </SearchContext.Provider>
  );
};

export const useSearch = () => {
  const context = useContext(SearchContext);
  if (!context) {
    throw new Error('useSearch must be used within a SearchProvider');
  }
  return context;
};
