import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext.js';
import { FiPackage, FiClock, FiCalendar, FiMapPin } from 'react-icons/fi';
import './Orders.css';

const Orders = () => {
  const { user } = useAuth();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Mock order data for development
  const MOCK_ORDERS = [
    {
      _id: 'ord-' + Math.random().toString(36).substr(2, 6),
      status: 'Delivered',
      items: [
        { _id: 'item1', name: 'Butter Chicken', quantity: 1, price: 299 },
        { _id: 'item2', name: 'Garlic Naan', quantity: 2, price: 49 }
      ],
      restaurant: {
        name: 'Punjabi Tadka',
        address: '123 Main St, Delhi, India'
      },
      total: 397,
      deliveryAddress: '456 Park Avenue, Mumbai, India',
      createdAt: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000).toISOString(),
      deliveryTime: '30-40 min'
    },
    {
      _id: 'ord-' + Math.random().toString(36).substr(2, 6),
      status: 'Processing',
      items: [
        { _id: 'item3', name: 'Masala Dosa', quantity: 1, price: 149 },
        { _id: 'item4', name: 'Filter Coffee', quantity: 2, price: 79 }
      ],
      restaurant: {
        name: 'South Indian Delights',
        address: '789 MG Road, Bangalore, India'
      },
      total: 307,
      deliveryAddress: '456 Park Avenue, Mumbai, India',
      createdAt: new Date().toISOString(),
      deliveryTime: '45-55 min'
    }
  ];

  useEffect(() => {
    // Simulate API call with mock data
    const fetchOrders = async () => {
      try {
        // In a real app, we would fetch from an API
        // const response = await axios.get(`/api/orders/user/${user.id}`);
        
        // For development, use mock data
        setTimeout(() => {
          setOrders(MOCK_ORDERS);
          setLoading(false);
        }, 1000);
      } catch (err) {
        setError('Failed to fetch orders');
        setLoading(false);
      }
    };

    if (user) {
      fetchOrders();
    } else {
      setLoading(false);
      setError('Please log in to view your orders');
    }
  }, [user]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading">Loading your orders...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="orders-container">
      <div className="orders-header">
        <h1>My Orders</h1>
        <p>View and track your orders</p>
      </div>
      
      {orders.length === 0 ? (
        <div className="no-orders">
          <div className="no-orders-icon">
            <FiPackage />
          </div>
          <h2>No orders yet</h2>
          <p>You haven't placed any orders yet. Hungry? Order some delicious food!</p>
        </div>
      ) : (
        <div className="orders-list">
          {orders.map(order => (
            <div key={order._id} className="order-card">
              <div className="order-header">
                <div className="order-id">
                  <h3>Order #{order._id.slice(-6)}</h3>
                  <span className={`status ${order.status.toLowerCase()}`}>
                    {order.status}
                  </span>
                </div>
                <div className="order-date">
                  <FiCalendar className="order-icon" />
                  {new Date(order.createdAt).toLocaleDateString('en-US', {
                    year: 'numeric',
                    month: 'short',
                    day: 'numeric'
                  })}
                </div>
              </div>
              
              <div className="order-details">
                <div className="restaurant-info">
                  <h4>{order.restaurant.name}</h4>
                  <p>
                    <FiMapPin className="order-icon" />
                    {order.restaurant.address}
                  </p>
                </div>
                
                <div className="delivery-info">
                  <div className="delivery-time">
                    <FiClock className="order-icon" />
                    <span>Delivery Time: {order.deliveryTime}</span>
                  </div>
                  <div className="delivery-address">
                    <FiMapPin className="order-icon" />
                    <span>Delivered to: {order.deliveryAddress}</span>
                  </div>
                </div>
                
                <div className="order-items">
                  <h5>Order Items</h5>
                  <table className="items-table">
                    <thead>
                      <tr>
                        <th>Item</th>
                        <th>Qty</th>
                        <th>Price</th>
                      </tr>
                    </thead>
                    <tbody>
                      {order.items.map(item => (
                        <tr key={item._id} className="order-item">
                          <td className="item-name">{item.name}</td>
                          <td className="item-quantity">{item.quantity}</td>
                          <td className="item-price">₹{item.price.toFixed(2)}</td>
                        </tr>
                      ))}
                    </tbody>
                    <tfoot>
                      <tr className="order-total">
                        <td colSpan="2">Total</td>
                        <td>₹{order.total.toFixed(2)}</td>
                      </tr>
                    </tfoot>
                  </table>
                </div>
              </div>
              
              <div className="order-actions">
                <button className="order-action-btn primary">Reorder</button>
                <button className="order-action-btn secondary">Get Help</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Orders;
