import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiMail, FiLock, FiUser, FiPhone, FiMapPin, FiHome } from 'react-icons/fi';
import { FcGoogle } from 'react-icons/fc';
import './Register.css';

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    name: '',
    email: '',
    mobile: '',
    password: '',
    confirmPassword: '',
    address: {
      street: '',
      city: '',
      pincode: '',
      state: '',
      country: ''
    }
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [usernameError, setUsernameError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      // Handle nested address fields
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    // Clear errors when user types
    if (error) setError('');
    if (name === 'username') setUsernameError('');
  };

  // Check username availability
  const checkUsername = async (username) => {
    try {
      const response = await fetch(`http://localhost:5001/api/auth/check-username/${username}`);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to check username');
      }

      if (data.exists) {
        setUsernameError('Username already taken');
        return false;
      }
      return true;
    } catch (error) {
      setUsernameError('Error checking username availability');
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setUsernameError('');
    setLoading(true);

    try {
      // Validate input
      if (!formData.username || !formData.name || !formData.email || !formData.mobile || !formData.password || !formData.confirmPassword) {
        throw new Error('All required fields must be filled');
      }

      // Validate username format
      const usernameRegex = /^[a-zA-Z0-9_]{3,20}$/;
      if (!usernameRegex.test(formData.username)) {
        throw new Error('Username must be 3-20 characters long and can only contain letters, numbers, and underscores');
      }

      // Check username availability
      const isUsernameAvailable = await checkUsername(formData.username);
      if (!isUsernameAvailable) {
        setLoading(false);
        return;
      }

      if (formData.password !== formData.confirmPassword) {
        throw new Error('Passwords do not match');
      }

      // Validate mobile number
      const mobileRegex = /^[0-9]{10}$/;
      if (!mobileRegex.test(formData.mobile)) {
        throw new Error('Please enter a valid 10-digit mobile number');
      }

      const response = await fetch('http://localhost:5001/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          username: formData.username,
          name: formData.name,
          email: formData.email,
          mobile: formData.mobile,
          password: formData.password,
          address: formData.address
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to create account');
      }

      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      navigate('/');
    } catch (error) {
      setError(error.message || 'Failed to create account');
    } finally {
      setLoading(false);
    }
  };

  const handleSocialRegister = (platform) => {
    alert(`${platform} registration is not implemented yet.`);
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-header">
          <h1>Create Account</h1>
          <p>Join us for a delicious food journey</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="register-form">
          <div className="form-section">
            <h2>Basic Information</h2>
            <div className="form-group">
              <label htmlFor="username">Username *</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="username"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  placeholder="Choose a username"
                  required
                  disabled={loading}
                  className={usernameError ? 'error' : ''}
                />
                <FiUser className="icon" />
              </div>
              {usernameError && <div className="field-error">{usernameError}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="name">Full Name *</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Enter your full name"
                  required
                  disabled={loading}
                />
                <FiUser className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="email">Email Address *</label>
              <div className="input-wrapper">
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  required
                  disabled={loading}
                />
                <FiMail className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="mobile">Mobile Number *</label>
              <div className="input-wrapper">
                <input
                  type="tel"
                  id="mobile"
                  name="mobile"
                  value={formData.mobile}
                  onChange={handleChange}
                  placeholder="Enter your mobile number"
                  required
                  disabled={loading}
                />
                <FiPhone className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="password">Password *</label>
              <div className="input-wrapper">
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Create a password"
                  required
                  disabled={loading}
                />
                <FiLock className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password *</label>
              <div className="input-wrapper">
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  placeholder="Confirm your password"
                  required
                  disabled={loading}
                />
                <FiLock className="icon" />
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Address Information </h2>
            <div className="form-group">
              <label htmlFor="street">Street Address</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="street"
                  name="address.street"
                  value={formData.address.street}
                  onChange={handleChange}
                  placeholder="Enter your street address"
                  disabled={loading}
                />
                <FiHome className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="city">City</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="city"
                  name="address.city"
                  value={formData.address.city}
                  onChange={handleChange}
                  placeholder="Enter your city"
                  disabled={loading}
                />
                <FiMapPin className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="pincode">Pincode / ZIP Code</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="pincode"
                  name="address.pincode"
                  value={formData.address.pincode}
                  onChange={handleChange}
                  placeholder="Enter your pincode"
                  disabled={loading}
                />
                <FiMapPin className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="state">State</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="state"
                  name="address.state"
                  value={formData.address.state}
                  onChange={handleChange}
                  placeholder="Enter your state"
                  disabled={loading}
                />
                <FiMapPin className="icon" />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="country">Country</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="country"
                  name="address.country"
                  value={formData.address.country}
                  onChange={handleChange}
                  placeholder="Enter your country"
                  disabled={loading}
                />
                <FiMapPin className="icon" />
              </div>
            </div>
          </div>

          <button 
            type="submit" 
            className="register-btn"
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <div className="social-login">
          <p>Or register with</p>
          <div className="social-buttons">
            <button 
              className="social-btn" 
              onClick={() => handleSocialRegister('Google')}
              type="button"
            >
              <FcGoogle /> Google
            </button>
          </div>
        </div>

        <div className="login-link">
          Already have an account? <Link to="/login">Sign in here</Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
