import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.js';
import { useCart } from '../context/CartContext.js';
import { FiHome, FiShoppingBag, FiUser, FiShoppingCart, FiLogOut, FiMenu, FiCoffee, FiPackage } from 'react-icons/fi';
import LocationSelector from './LocationSelector.js';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const { getCartItemCount } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [userLocation, setUserLocation] = useState(null);

  // Load saved location on component mount
  useEffect(() => {
    const savedLocation = localStorage.getItem('userLocation');
    const savedCoordinates = localStorage.getItem('userCoordinates');

    if (savedLocation) {
      setUserLocation({
        address: savedLocation,
        coordinates: savedCoordinates ? JSON.parse(savedCoordinates) : null
      });
    }
  }, []);

  const handleLocationSelect = (locationData) => {
    setUserLocation(locationData);
    // You could also dispatch this to a global state/context if needed
    console.log('Location selected:', locationData);
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Close menus when clicking outside
  useEffect(() => {
    const closeMenus = () => {
      setIsMenuOpen(false);
    };

    document.addEventListener('click', closeMenus);
    return () => document.removeEventListener('click', closeMenus);
  }, []);

  // Prevent closing when clicking on the menus themselves
  const handleMenuClick = (e) => {
    e.stopPropagation();
  };

  // Close mobile menu when changing routes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location.pathname]);

  return (
    <nav className="navbar">
      <div className="nav-content">
        <div className="nav-left">
          <Link to="/" className="nav-logo">
            FoodDelivery
          </Link>
          <LocationSelector onSelectLocation={handleLocationSelect} />
          <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
            <FiMenu />
          </button>
        </div>

        <div className={`nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
          <Link to="/" className="nav-link">
            <FiHome className="nav-icon" /> Home
          </Link>

          <Link to="/restaurants/order-online" className="nav-link">
            <FiPackage className="nav-icon" /> Order Online
          </Link>

          <Link to="/restaurants/dine-out" className="nav-link">
            <FiCoffee className="nav-icon" /> Dine Out
          </Link>

          <Link to="/cart" className="cart-link nav-link">
            <FiShoppingCart className="nav-icon" />
            <span>Cart</span>
            {getCartItemCount() > 0 && (
              <span className="cart-count">{getCartItemCount()}</span>
            )}
          </Link>

          {user ? (
            <div className="user-menu" onClick={handleMenuClick}>
              <button onClick={toggleMenu} className="user-menu-btn">
                <FiUser className="nav-icon" />
                <span>{user.name || 'Account'}</span>
              </button>
              {isMenuOpen && (
                <div className="dropdown-menu">
                  <Link to="/profile" className="dropdown-item">
                    <FiUser /> Profile
                  </Link>
                  <Link to="/orders" className="dropdown-item">
                    <FiShoppingBag /> Orders
                  </Link>
                  <button onClick={handleLogout} className="dropdown-item">
                    <FiLogOut /> Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="login-btn">Login</Link>
              <Link to="/register" className="register-btn">Register</Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
