import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiStar, FiFilter, FiClock, FiChevronDown } from 'react-icons/fi';
import './DineOut.css';

const DineOut = () => {
  const [activeTab, setActiveTab] = useState('dineout');
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [showSortDropdown, setShowSortDropdown] = useState(false);
  const [showBookingForm, setShowBookingForm] = useState(false);
  const [bookingDetails, setBookingDetails] = useState({
    restaurantId: null,
    date: '',
    time: '',
    guests: 2
  });
  
  // Active filters
  const [filters, setFilters] = useState({
    offers: false,
    fastDelivery: false,
    newOnSwiggy: false,
    ratings4Plus: false,
    pureVeg: false,
    range300to600: false,
    lessThan300: false
  });

  // Mock data for dine-out restaurants
  const mockRestaurants = [
    {
      id: 1,
      name: 'Chinese Wok',
      image: 'https://images.unsplash.com/photo-1552566626-52f8b828add9',
      rating: 4.4,
      deliveryTime: '25-30 mins',
      cuisines: ['Chinese', 'Asian', 'Tibetan', 'Desserts'],
      location: 'The Palace, Godadara Road',
      discount: '66% OFF UPTO ‚Çπ126'
    },
    {
      id: 2,
      name: 'Pizza Hut',
      image: 'https://images.unsplash.com/photo-1513104890138-7c749659a591',
      rating: 4.2,
      deliveryTime: '30-35 mins',
      cuisines: ['Pizzas'],
      location: 'Athwa',
      discount: '50% OFF UPTO ‚Çπ100'
    },
    {
      id: 3,
      name: 'Olio - The Wood Fired Pizzeria',
      image: 'https://images.unsplash.com/photo-1574071318508-1cdbab80d002',
      rating: 4.2,
      deliveryTime: '35-40 mins',
      cuisines: ['Pizzas', 'Pastas', 'Italian', 'Fast Food'],
      location: 'Jivkor Nagar Co.Op.Hs',
      discount: '66% OFF UPTO ‚Çπ126'
    },
    {
      id: 4,
      name: 'Chhote Miyan',
      image: 'https://images.unsplash.com/photo-1633237308525-cd587cf71926',
      rating: 4.3,
      deliveryTime: '20-25 mins',
      cuisines: ['Biryani', 'North Indian', 'Mughlai'],
      location: 'Athwa',
      discount: '‚Çπ66 OFF ABOVE ‚Çπ299'
    }
  ];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setRestaurants(mockRestaurants);
      setFilteredRestaurants(mockRestaurants);
      setLoading(false);
    }, 800);
  }, []);

  useEffect(() => {
    applyFilters();
  }, [filters, searchQuery, restaurants]);

  const applyFilters = () => {
    let result = [...restaurants];
    
    // Apply search filter
    if (searchQuery.trim()) {
      result = result.filter(restaurant => 
        restaurant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        restaurant.cuisines.join(', ').toLowerCase().includes(searchQuery.toLowerCase()) ||
        restaurant.location.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }
    
    // Apply rating filter
    if (filters.ratings4Plus) {
      result = result.filter(restaurant => restaurant.rating >= 4);
    }
    
    // Apply distance filter
    if (filters.range300to600) {
      result = result.filter(restaurant => restaurant.discount.includes('‚Çπ300-‚Çπ600'));
    }
    
    // Apply veg filter
    if (filters.pureVeg) {
      result = result.filter(restaurant => restaurant.cuisines.includes('Vegetarian') || restaurant.cuisines.includes('Veg'));
    }
    
    setFilteredRestaurants(result);
  };

  const handleFilterChange = (filterName) => {
    setFilters(prev => ({
      ...prev,
      [filterName]: !prev[filterName]
    }));
  };

  const getActiveFiltersCount = () => {
    return Object.values(filters).filter(Boolean).length;
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // The actual filtering is done in the useEffect/applyFilters
    console.log("Search applied for:", searchQuery);
  };

  const handleSort = (sortBy) => {
    let sortedRestaurants = [...filteredRestaurants];
    
    switch(sortBy) {
      case 'ratingHighToLow':
        sortedRestaurants.sort((a, b) => b.rating - a.rating);
        break;
      case 'ratingLowToHigh':
        sortedRestaurants.sort((a, b) => a.rating - b.rating);
        break;
      case 'distanceNearToFar':
        sortedRestaurants.sort((a, b) => a.distance - b.distance);
        break;
      default:
        // Default sort: no change
        break;
    }
    
    setFilteredRestaurants(sortedRestaurants);
    setShowSortDropdown(false);
  };
  
  const handleBookTableClick = (restaurantId) => {
    setBookingDetails({
      ...bookingDetails,
      restaurantId
    });
    setShowBookingForm(true);
  };
  
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    // In a real app, this would send the booking to an API
    const restaurant = restaurants.find(r => r.id === bookingDetails.restaurantId);
    alert(`Table booked at ${restaurant?.name} for ${bookingDetails.guests} guests on ${bookingDetails.date} at ${bookingDetails.time}`);
    setShowBookingForm(false);
  };

  return (
    <div className="dine-out-page">
      <div className="hero-banner">
        <div className="hero-overlay">
          <h1>Restaurants in your city</h1>
        </div>
      </div>

      <div className="container">
        <div className="page-tabs">
          <Link 
            to="/restaurants/dine-out"
            className={`tab ${activeTab === 'dineout' ? 'active' : ''}`}
            onClick={() => setActiveTab('dineout')}
          >
            Dine Out
          </Link>
          <Link 
            to="/restaurants/order-online"
            className={`tab ${activeTab === 'order' ? 'active' : ''}`}
            onClick={() => setActiveTab('order')}
          >
            Order Online
          </Link>
        </div>

        <div className="filters-section">
          <div className="filter-pills">
            <button className="filter-btn">
              <FiFilter /> Filter {getActiveFiltersCount() > 0 && <span className="filter-count">{getActiveFiltersCount()}</span>}
            </button>
            
            <div className="filter-dropdown">
              <button 
                className="filter-pill" 
                onClick={() => setShowSortDropdown(!showSortDropdown)}
              >
                Sort By <FiChevronDown />
              </button>
              {showSortDropdown && (
                <div className="dropdown-menu">
                  <button onClick={() => handleSort('ratingHighToLow')}>Rating: High to Low</button>
                  <button onClick={() => handleSort('ratingLowToHigh')}>Rating: Low to High</button>
                  <button onClick={() => handleSort('distanceNearToFar')}>Distance: Near to Far</button>
                </div>
              )}
            </div>
            
            <button 
              className={`filter-pill ${showBookingForm ? 'active' : ''}`}
              onClick={() => setShowBookingForm(!showBookingForm)}
            >
              Book a table
            </button>
            
            <button 
              className={`filter-pill ${filters.range300to600 ? 'active' : ''}`}
              onClick={() => handleFilterChange('range300to600')}
            >
              Rs. 300-Rs. 600
            </button>
            
            <button 
              className={`filter-pill ${filters.pureVeg ? 'active' : ''}`} 
              onClick={() => handleFilterChange('pureVeg')}
            >
              Pure Veg
            </button>
          </div>

          <form className="search-form" onSubmit={handleSearch}>
            <input 
              type="text" 
              placeholder="Search for restaurants" 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>
        
        {showBookingForm && (
          <div className="booking-form-container">
            <form onSubmit={handleBookingSubmit} className="booking-form">
              <h3>Book a Table</h3>
              
              <div className="form-group">
                <label>Select Restaurant</label>
                <select 
                  value={bookingDetails.restaurantId || ''} 
                  onChange={(e) => setBookingDetails({...bookingDetails, restaurantId: parseInt(e.target.value)})}
                  required
                >
                  <option value="">Select a restaurant</option>
                  {restaurants.map(r => (
                    <option key={r.id} value={r.id}>{r.name}</option>
                  ))}
                </select>
              </div>
              
              <div className="form-group">
                <label>Date</label>
                <input 
                  type="date" 
                  value={bookingDetails.date}
                  onChange={(e) => setBookingDetails({...bookingDetails, date: e.target.value})}
                  min={new Date().toISOString().split('T')[0]}
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Time</label>
                <input 
                  type="time" 
                  value={bookingDetails.time}
                  onChange={(e) => setBookingDetails({...bookingDetails, time: e.target.value})}
                  required
                />
              </div>
              
              <div className="form-group">
                <label>Number of Guests</label>
                <input 
                  type="number" 
                  min="1" 
                  max="20"
                  value={bookingDetails.guests}
                  onChange={(e) => setBookingDetails({...bookingDetails, guests: parseInt(e.target.value)})}
                  required
                />
              </div>
              
              <div className="form-actions">
                <button type="button" onClick={() => setShowBookingForm(false)} className="btn-cancel">
                  Cancel
                </button>
                <button type="submit" className="btn-submit">
                  Book Now
                </button>
              </div>
            </form>
          </div>
        )}

        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Finding great places to dine...</p>
          </div>
        ) : filteredRestaurants.length === 0 ? (
          <div className="no-results">
            <h3>No restaurants found</h3>
            <p>Try changing your filters or search query</p>
          </div>
        ) : (
          <div className="restaurants-grid">
            {filteredRestaurants.map(restaurant => (
              <div key={restaurant.id} className="restaurant-card">
                <div className="restaurant-image-container">
                  <img src={restaurant.image} alt={restaurant.name} className="restaurant-image" />
                  {restaurant.discount && (
                    <div className="restaurant-badge">{restaurant.discount}</div>
                  )}
                </div>
                <div className="restaurant-details">
                  <h3 className="restaurant-name">{restaurant.name}</h3>
                  <div className="restaurant-rating">
                    <span className={`rating-badge ${restaurant.rating >= 4 ? 'high' : restaurant.rating >= 3 ? 'medium' : 'low'}`}>
                      <FiStar /> {restaurant.rating}
                    </span>
                  </div>
                  <p className="restaurant-cuisine">{restaurant.cuisines.join(', ')}</p>
                  <div className="restaurant-location">
                    <FiMapPin /> {restaurant.location}
                  </div>
                  <div className="restaurant-price">{restaurant.discount}</div>
                  
                  <div className="restaurant-features">
                    {restaurant.cuisines.includes('Vegetarian') || restaurant.cuisines.includes('Veg') && (
                      <div className="veg-badge">
                        <div className="veg-icon"></div> Pure Vegetarian
                      </div>
                    )}
                    
                    {restaurant.discount && (
                      <button 
                        className="book-table-btn"
                        onClick={() => handleBookTableClick(restaurant.id)}
                      >
                        <span className="icon">üçΩÔ∏è</span> Book a table
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DineOut;
