import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Checkout.css';

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [deliveryAddress, setDeliveryAddress] = useState('1341 Morris Street');
  const [deliveryTime, setDeliveryTime] = useState('40 mins');
  const [distance, setDistance] = useState('4 kms');
  const [subTotal, setSubTotal] = useState(0);
  const [deliveryFee, setDeliveryFee] = useState(9);
  const [total, setTotal] = useState(0);
  const [couponCode, setCouponCode] = useState('');
  const [showCouponInput, setShowCouponInput] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    const savedCart = JSON.parse(localStorage.getItem('cart') || '[]');
    setCart(savedCart);

    // Calculate subtotal
    const subtotal = savedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(subtotal);
    setTotal(subtotal + deliveryFee);
  }, []);

  const handleQuantityChange = (itemId, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = cart.map(item => {
      if (item.id === itemId) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });

    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handleRemoveItem = (itemId) => {
    const updatedCart = cart.filter(item => item.id !== itemId);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handleAddCoupon = () => {
    // Add coupon logic here
    alert('Coupon functionality will be implemented soon!');
  };

  const handleCheckout = () => {
    // Implement checkout logic here
    if (cart.length === 0) {
      alert('Your cart is empty!');
      return;
    }

    // You can add payment integration here
    alert('Order placed successfully!');
    localStorage.removeItem('cart');
    navigate('/orders');
  };

  return (
    <div className="checkout-container">
      <div className="checkout-content">
        {/* Delivery Address Section */}
        <div className="delivery-info">
          <h2>Delivery address</h2>
          <div className="address-selector">
            <div className="selected-address">
              <span>{deliveryAddress}</span>
              <button className="change-address-btn">
                <i className="fas fa-chevron-down"></i>
              </button>
            </div>
            <div className="delivery-meta">
              <span><i className="far fa-clock"></i> {deliveryTime}</span>
              <span><i className="fas fa-map-marker-alt"></i> {distance}</span>
            </div>
          </div>
        </div>

        {/* Order Items Section */}
        <div className="order-items">
          {cart.map(item => (
            <div key={item.id} className="order-item">
              <img src={item.image} alt={item.name} className="item-image" />
              <div className="item-details">
                <h3>{item.name}</h3>
                <p className="item-extras">{item.description}</p>
              </div>
              <div className="item-actions">
                <div className="quantity-controls">
                  <button 
                    onClick={() => handleRemoveItem(item.id)}
                    className="remove-btn"
                  >
                    Ã—
                  </button>
                  <div className="quantity-selector">
                    <input
                      type="number"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                      min="1"
                    />
                  </div>
                </div>
                <span className="item-price">${(item.price * item.quantity).toFixed(2)}</span>
              </div>
            </div>
          ))}
        </div>

        {/* Order Summary Section */}
        <div className="order-summary">
          <div className="summary-row">
            <span>Sub Total</span>
            <span>${subTotal.toFixed(2)}</span>
          </div>
          <div className="summary-row">
            <span>Delivery Fee</span>
            <span>${deliveryFee.toFixed(2)}</span>
          </div>

          {/* Coupon Section */}
          <div className="coupon-section">
            {showCouponInput ? (
              <div className="coupon-input">
                <input
                  type="text"
                  placeholder="Enter coupon code"
                  value={couponCode}
                  onChange={(e) => setCouponCode(e.target.value)}
                />
                <button onClick={handleAddCoupon}>Add Coupon</button>
              </div>
            ) : (
              <button 
                className="find-promotion-btn"
                onClick={() => setShowCouponInput(true)}
              >
                <i className="fas fa-ticket-alt"></i>
                Find Promotion
              </button>
            )}
          </div>

          {/* Total Section */}
          <div className="total-row">
            <span>Total</span>
            <span>${total.toFixed(2)}</span>
          </div>

          {/* Checkout Button */}
          <button 
            className="checkout-btn"
            onClick={handleCheckout}
            disabled={cart.length === 0}
          >
            Check out
          </button>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
