import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiHeart } from 'react-icons/fi';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import './Favorites.css';

// Sample data for development only
const sampleRestaurants = [
  {
    id: 'r1',
    name: 'Taj Mahal Indian Cuisine',
    image: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4',
    cuisineType: 'Indian',
    rating: 4.7,
    deliveryTime: '30-40 min',
    deliveryFee: '₹40'
  },
  {
    id: 'r2',
    name: 'Dragon House',
    image: 'https://images.unsplash.com/photo-1552566626-52f8b828add9',
    cuisineType: 'Chinese',
    rating: 4.5,
    deliveryTime: '25-35 min',
    deliveryFee: '₹50'
  },
  {
    id: 'r3',
    name: 'Pizza Paradise',
    image: 'https://images.unsplash.com/photo-1513104890138-7c749659a591',
    cuisineType: 'Italian',
    rating: 4.8,
    deliveryTime: '20-30 min',
    deliveryFee: '₹30'
  }
];

const sampleFoodItems = [
  {
    id: 'f1',
    name: 'Butter Chicken',
    image: 'https://images.unsplash.com/photo-1588166524941-3bf61a9c41db',
    restaurantName: 'Taj Mahal Indian Cuisine',
    price: 280,
    rating: 4.9,
    description: 'Tender chicken in a rich, creamy tomato sauce'
  },
  {
    id: 'f2',
    name: 'Kung Pao Chicken',
    image: 'https://images.unsplash.com/photo-1525755662778-989d0524087e',
    restaurantName: 'Dragon House',
    price: 220,
    rating: 4.6,
    description: 'Spicy stir-fried chicken with peanuts and vegetables'
  },
  {
    id: 'f3',
    name: 'Margherita Pizza',
    image: 'https://images.unsplash.com/photo-1604382354936-07c5d9983bd3',
    restaurantName: 'Pizza Paradise',
    price: 350,
    rating: 4.8,
    description: 'Classic pizza with tomato sauce, mozzarella, and basil'
  }
];

const Favorites = () => {
  const [activeTab, setActiveTab] = useState('restaurants');
  const { user, isFavorite, toggleFavorite } = useAuth();
  const { addToCart } = useCart();
  const [favoriteRestaurants, setFavoriteRestaurants] = useState([]);
  const [favoriteFoodItems, setFavoriteFoodItems] = useState([]);

  useEffect(() => {
    // In a real app, you would fetch from API
    // For now, we'll filter the sample data based on user favorites
    if (user && user.favorites) {
      const restaurants = sampleRestaurants.filter(restaurant => 
        isFavorite(restaurant.id, 'restaurant')
      );
      setFavoriteRestaurants(restaurants);

      const foodItems = sampleFoodItems.filter(item => 
        isFavorite(item.id, 'item')
      );
      setFavoriteFoodItems(foodItems);
    }
  }, [user, isFavorite]);

  const handleAddToCart = (item) => {
    addToCart({
      id: item.id,
      name: item.name,
      price: item.price,
      restaurantName: item.restaurantName,
      quantity: 1,
      image: item.image
    });
  };

  const handleToggleFavorite = (id, type) => {
    toggleFavorite(id, type);
  };

  // For demo purposes, add sample restaurants if none are favorited
  const displayRestaurants = favoriteRestaurants.length > 0 
    ? favoriteRestaurants 
    : sampleRestaurants.slice(0, 2);

  // For demo purposes, add sample food items if none are favorited
  const displayFoodItems = favoriteFoodItems.length > 0 
    ? favoriteFoodItems 
    : sampleFoodItems.slice(0, 2);

  return (
    <div className="favorites-container">
      <div className="favorites-header">
        <h1>My Favorites</h1>
        <p>Your favorite restaurants and dishes in one place</p>
      </div>

      <div className="favorites-tabs">
        <button 
          className={activeTab === 'restaurants' ? 'active' : ''} 
          onClick={() => setActiveTab('restaurants')}
        >
          Restaurants
        </button>
        <button 
          className={activeTab === 'dishes' ? 'active' : ''} 
          onClick={() => setActiveTab('dishes')}
        >
          Dishes
        </button>
      </div>

      {activeTab === 'restaurants' ? (
        <div className="favorites-list">
          {displayRestaurants.length > 0 ? (
            displayRestaurants.map(restaurant => (
              <div key={restaurant.id} className="favorite-card restaurant-card">
                <div className="favorite-image">
                  <img src={restaurant.image} alt={restaurant.name} />
                  <button 
                    className={`favorite-btn ${isFavorite(restaurant.id, 'restaurant') ? 'favorited' : ''}`}
                    onClick={() => handleToggleFavorite(restaurant.id, 'restaurant')}
                  >
                    <FiHeart />
                  </button>
                </div>
                <div className="favorite-details">
                  <h3>{restaurant.name}</h3>
                  <div className="restaurant-info">
                    <span className="cuisine-type">{restaurant.cuisineType}</span>
                    <span className="rating">★ {restaurant.rating}</span>
                  </div>
                  <div className="delivery-info">
                    <span>{restaurant.deliveryTime}</span>
                    <span>{restaurant.deliveryFee}</span>
                  </div>
                  <Link to={`/restaurant/${restaurant.id}`} className="view-btn">View Restaurant</Link>
                </div>
              </div>
            ))
          ) : (
            <div className="empty-state">
              <h3>No favorite restaurants yet</h3>
              <p>Explore restaurants and add them to your favorites!</p>
              <Link to="/" className="explore-btn">Explore Restaurants</Link>
            </div>
          )}
        </div>
      ) : (
        <div className="favorites-list">
          {displayFoodItems.length > 0 ? (
            displayFoodItems.map(item => (
              <div key={item.id} className="favorite-card food-card">
                <div className="favorite-image">
                  <img src={item.image} alt={item.name} />
                  <button 
                    className={`favorite-btn ${isFavorite(item.id, 'item') ? 'favorited' : ''}`}
                    onClick={() => handleToggleFavorite(item.id, 'item')}
                  >
                    <FiHeart />
                  </button>
                </div>
                <div className="favorite-details">
                  <h3>{item.name}</h3>
                  <p className="restaurant-name">{item.restaurantName}</p>
                  <p className="description">{item.description}</p>
                  <div className="price-rating">
                    <span className="price">₹{item.price}</span>
                    <span className="rating">★ {item.rating}</span>
                  </div>
                  <div className="action-buttons">
                    <button className="add-to-cart-btn" onClick={() => handleAddToCart(item)}>
                      Add to Cart
                    </button>
                    <Link to={`/restaurant/${item.id.replace('f', 'r')}`} className="view-btn">
                      View Restaurant
                    </Link>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="empty-state">
              <h3>No favorite dishes yet</h3>
              <p>Explore restaurants and add dishes to your favorites!</p>
              <Link to="/" className="explore-btn">Explore Restaurants</Link>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Favorites;
