import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.js';
import { useCart } from '../context/CartContext.js';
import { FiHome, FiShoppingBag, FiUser, FiShoppingCart, FiLogOut, FiMenu, FiChevronDown } from 'react-icons/fi';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const { getCartItemCount } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isRestaurantMenuOpen, setIsRestaurantMenuOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Mock restaurant categories - replace with actual data when available
  const restaurantCategories = [
    { id: 'indian', name: 'Indian', count: 12 },
    { id: 'italian', name: 'Italian', count: 8 },
    { id: 'japanese', name: 'Japanese', count: 5 },
    { id: 'mexican', name: 'Mexican', count: 7 },
    { id: 'chinese', name: 'Chinese', count: 10 },
    { id: 'thai', name: 'Thai', count: 4 },
    { id: 'american', name: 'American', count: 15 }
  ];

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleRestaurantMenu = () => {
    setIsRestaurantMenuOpen(!isRestaurantMenuOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Close menus when clicking outside
  useEffect(() => {
    const closeMenus = () => {
      setIsMenuOpen(false);
      setIsRestaurantMenuOpen(false);
    };

    document.addEventListener('click', closeMenus);
    return () => document.removeEventListener('click', closeMenus);
  }, []);

  // Prevent closing when clicking on the menus themselves
  const handleMenuClick = (e) => {
    e.stopPropagation();
  };

  // Close mobile menu when changing routes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location.pathname]);

  return (
    <nav className="navbar">
      <div className="nav-content">
        <div className="nav-left">
          <Link to="/" className="nav-logo">
            FoodDelivery
          </Link>
          <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
            <FiMenu />
          </button>
        </div>

        <div className={`nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
          <Link to="/" className="nav-link">
            <FiHome className="nav-icon" /> Home
          </Link>

          <div className="nav-dropdown" onClick={handleMenuClick}>
            <button className="nav-link dropdown-trigger" onClick={toggleRestaurantMenu}>
              <FiShoppingBag className="nav-icon" />
              Restaurants
              <FiChevronDown className={`dropdown-arrow ${isRestaurantMenuOpen ? 'open' : ''}`} />
            </button>

            {isRestaurantMenuOpen && (
              <div className="restaurant-dropdown-menu">
                <div className="restaurant-categories">
                  <h3>Cuisines</h3>
                  <div className="category-grid">
                    {restaurantCategories.map(category => (
                      <Link 
                        key={category.id} 
                        to={`/restaurants/category/${category.id}`} 
                        className="category-item"
                      >
                        {category.name}
                        <span className="category-count">{category.count}</span>
                      </Link>
                    ))}
                  </div>
                  <div className="dropdown-footer">
                    <Link to="/restaurants" className="view-all-link">
                      View All Restaurants
                    </Link>
                  </div>
                </div>
                <div className="featured-restaurants">
                  <h3>Popular Restaurants</h3>
                  <div className="featured-list">
                    <Link to="/restaurant/1" className="featured-restaurant">
                      <div className="featured-img" style={{backgroundImage: 'url(https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=60)'}}></div>
                      <span>Tasty Bites</span>
                    </Link>
                    <Link to="/restaurant/2" className="featured-restaurant">
                      <div className="featured-img" style={{backgroundImage: 'url(https://images.unsplash.com/photo-1555396273-367ea4eb4db5?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=60)'}}></div>
                      <span>Pizza Paradise</span>
                    </Link>
                    <Link to="/restaurant/3" className="featured-restaurant">
                      <div className="featured-img" style={{backgroundImage: 'url(https://images.unsplash.com/photo-1559339352-11d035aa65de?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=60)'}}></div>
                      <span>Sushi Master</span>
                    </Link>
                  </div>
                </div>
              </div>
            )}
          </div>

          <Link to="/cart" className="cart-link nav-link">
            <FiShoppingCart className="nav-icon" />
            <span>Cart</span>
            {getCartItemCount() > 0 && (
              <span className="cart-count">{getCartItemCount()}</span>
            )}
          </Link>

          {user ? (
            <div className="user-menu" onClick={handleMenuClick}>
              <button onClick={toggleMenu} className="user-menu-btn">
                <FiUser className="nav-icon" />
                <span>{user.name || 'Account'}</span>
                <FiChevronDown className={`dropdown-arrow ${isMenuOpen ? 'open' : ''}`} />
              </button>
              {isMenuOpen && (
                <div className="dropdown-menu">
                  <Link to="/profile" className="dropdown-item">
                    <FiUser /> Profile
                  </Link>
                  <Link to="/orders" className="dropdown-item">
                    <FiShoppingBag /> Orders
                  </Link>
                  <button onClick={handleLogout} className="dropdown-item">
                    <FiLogOut /> Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="login-btn">Login</Link>
              <Link to="/register" className="register-btn">Register</Link>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
