import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Checkout.css';

// Sample cart data
const sampleCartItems = [
  {
    id: 1,
    name: "Margherita Pizza",
    price: 12.99,
    quantity: 2,
    image: "https://images.unsplash.com/photo-1604382355076-af4b0eb60143?w=300&h=300&fit=crop"
  },
  {
    id: 2,
    name: "Chicken Burger",
    price: 8.99,
    quantity: 1,
    image: "https://images.unsplash.com/photo-1568901346375-23c9450c58cd?w=300&h=300&fit=crop"
  }
];

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [subTotal, setSubTotal] = useState(0);
  const [deliveryFee, setDeliveryFee] = useState(5.99); // Set a default delivery fee
  const [total, setTotal] = useState(0);
  const [promoCode, setPromoCode] = useState('');
  const [showCheckoutForm, setShowCheckoutForm] = useState(false);
  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zipCode: '',
    phone: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    let savedCart = JSON.parse(localStorage.getItem('cart') || '[]');

    // If cart is empty, initialize with sample data
    if (savedCart.length === 0) {
      savedCart = sampleCartItems;
      localStorage.setItem('cart', JSON.stringify(savedCart));
    }

    setCart(savedCart);

    // Calculate subtotal
    const subtotal = savedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(subtotal);
    setTotal(subtotal + deliveryFee);
  }, [deliveryFee]);

  const handleRemoveItem = (itemId) => {
    const updatedCart = cart.filter(item => item.id !== itemId);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = cart.map(item => {
      if (item.id === itemId) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });

    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handlePromoSubmit = (e) => {
    e.preventDefault();
    // Add promo code logic here
    alert('Promo code applied!');
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCheckout = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Create an order object
      const orderDetails = {
        orderId: 'ORD-' + Math.floor(100000 + Math.random() * 900000), // Generate a random order ID
        items: cart,
        subtotal: subTotal,
        deliveryFee: deliveryFee,
        total: total,
        deliveryAddress,
        paymentMethod,
        orderDate: new Date().toISOString(),
        status: 'Processing',
        estimatedDelivery: new Date(Date.now() + 45 * 60000).toISOString() // 45 minutes from now
      };

      // Store order details in localStorage for demo purposes
      // In a real app, you would send this to your backend API
      localStorage.setItem('currentOrder', JSON.stringify(orderDetails));

      // Clear cart
      localStorage.removeItem('cart');

      // Add delay to simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Navigate to order confirmation page with order details
      navigate('/order-confirmation', { state: { orderDetails } });
    } catch (error) {
      console.error('Error processing order:', error);
      alert('There was an error processing your order. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="checkout-page">
      {/* Header */}
      <header className="site-header">
        <div className="header-content">
          <Link to="/" className="logo">Checkout</Link>

          <div className="header-actions">
            <button className="search-btn">
              <i className="fas fa-search"></i>
            </button>
            <Link to="/cart" className="cart-btn">
              <i className="fas fa-shopping-basket"></i>
            </Link>
          </div>
        </div>
      </header>


      <div className="checkout-container">
        {!showCheckoutForm ? (
          <>
            {/* Cart Table */}
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Items</th>
                  <th>Title</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Total</th>
                  <th>Remove</th>
                </tr>
              </thead>
              <tbody>
                {cart.map(item => (
                  <tr key={item.id}>
                    <td>
                      <img src={item.image} alt={item.name} className="item-image" />
                    </td>
                    <td>{item.name}</td>
                    <td>₹{item.price.toFixed(2)}</td>
                    <td>
                      <input
                        type="number"
                        value={item.quantity}
                        onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                        min="1"
                      />
                    </td>
                    <td>₹{(item.price * item.quantity).toFixed(2)}</td>
                    <td>
                      <button onClick={() => handleRemoveItem(item.id)} className="remove-btn">
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="checkout-summary">
              {/* Cart Total */}
              <div className="cart-total">
                <h2>Cart Total</h2>
                <div className="total-row">
                  <span>Subtotal</span>
                  <span>₹{subTotal.toFixed(2)}</span>
                </div>
                <div className="total-row">
                  <span>Delivery Fee</span>
                  <span>₹{deliveryFee.toFixed(2)}</span>
                </div>
                <div className="total-row final-total">
                  <span>Total</span>
                  <span>₹{total.toFixed(2)}</span>
                </div>
                <button 
                  className="proceed-btn" 
                  onClick={() => setShowCheckoutForm(true)}
                  disabled={cart.length === 0}
                >
                  PROCEED TO CHECKOUT
                </button>
              </div>

              {/* Promo Code */}
              <div className="promo-section">
                <p>If you have a promo code, enter it here</p>
                <form className="promo-form" onSubmit={handlePromoSubmit}>
                  <input
                    type="text"
                    placeholder="Promo Code"
                    value={promoCode}
                    onChange={(e) => setPromoCode(e.target.value)}
                  />
                  <button type="submit">Submit</button>
                </form>
              </div>
            </div>
          </>
        ) : (
          <div className="checkout-form-container">
            <h2>Checkout Details</h2>
            <form onSubmit={handleCheckout} className="checkout-form">
              <div className="form-section">
                <h3>Delivery Address</h3>
                <div className="form-group">
                  <input
                    type="text"
                    name="street"
                    placeholder="Street Address"
                    value={deliveryAddress.street}
                    onChange={handleAddressChange}
                    required
                  />
                </div>
                <div className="form-row">
                  <div className="form-group">
                    <input
                      type="text"
                      name="city"
                      placeholder="City"
                      value={deliveryAddress.city}
                      onChange={handleAddressChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="text"
                      name="state"
                      placeholder="State"
                      value={deliveryAddress.state}
                      onChange={handleAddressChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <input
                      type="text"
                      name="zipCode"
                      placeholder="ZIP Code"
                      value={deliveryAddress.zipCode}
                      onChange={handleAddressChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-group">
                  <input
                    type="tel"
                    name="phone"
                    placeholder="Phone Number"
                    value={deliveryAddress.phone}
                    onChange={handleAddressChange}
                    required
                  />
                </div>
              </div>

              <div className="form-section">
                <h3>Payment Method</h3>
                <div className="payment-methods">
                  <label className="payment-method">
                    <input
                      type="radio"
                      name="payment"
                      value="card"
                      checked={paymentMethod === 'card'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                    />
                    <span>Credit/Debit Card</span>
                  </label>
                  <label className="payment-method">
                    <input
                      type="radio"
                      name="payment"
                      value="cash"
                      checked={paymentMethod === 'cash'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                    />
                    <span>Cash on Delivery</span>
                  </label>
                </div>

                {paymentMethod === 'card' && (
                  <div className="card-details">
                    <div className="form-group">
                      <input
                        type="text"
                        placeholder="Card Number"
                        required
                      />
                    </div>
                    <div className="form-row">
                      <div className="form-group">
                        <input
                          type="text"
                          placeholder="MM/YY"
                          required
                        />
                      </div>
                      <div className="form-group">
                        <input
                          type="text"
                          placeholder="CVV"
                          required
                        />
                      </div>
                    </div>
                  </div>
                )}
              </div>

              <div className="order-summary">
                <h3>Order Summary</h3>
                <div className="summary-row">
                  <span>Items Total:</span>
                  <span>₹{subTotal.toFixed(2)}</span>
                </div>
                <div className="summary-row">
                  <span>Delivery Fee:</span>
                  <span>₹{deliveryFee.toFixed(2)}</span>
                </div>
                <div className="summary-row total">
                  <span>Total Amount:</span>
                  <span>₹{total.toFixed(2)}</span>
                </div>
              </div>

              <div className="form-actions">
                <button type="button" className="back-btn" onClick={() => setShowCheckoutForm(false)}>
                  Back to Cart
                </button>
                <button type="submit" className="place-order-btn" disabled={isLoading}>
                  {isLoading ? 'Placing Order...' : 'Place Order'}
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Checkout;
