import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Checkout.css';

const Checkout = () => {
  const [cart, setCart] = useState([]);
  const [subTotal, setSubTotal] = useState(0);
  const [deliveryFee, setDeliveryFee] = useState(0);
  const [total, setTotal] = useState(0);
  const [promoCode, setPromoCode] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    let savedCart = JSON.parse(localStorage.getItem('cart') || '[]');

    // Add a sample item if cart is empty
    if (savedCart.length === 0) {
      const sampleItem = {
        id: 1,
        name: "Margherita Pizza",
        price: 12.99,
        quantity: 1,
        image: "https://images.unsplash.com/photo-1604382354936-07c5d9983bd3?w=200&h=200&fit=crop"
      };
      savedCart = [sampleItem];
      localStorage.setItem('cart', JSON.stringify(savedCart));
    }

    setCart(savedCart);

    // Calculate subtotal
    const subtotal = savedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(subtotal);
    setTotal(subtotal + deliveryFee);
  }, [deliveryFee]);

  const handleRemoveItem = (itemId) => {
    const updatedCart = cart.filter(item => item.id !== itemId);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handleQuantityChange = (itemId, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = cart.map(item => {
      if (item.id === itemId) {
        return { ...item, quantity: newQuantity };
      }
      return item;
    });

    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    // Recalculate totals
    const newSubTotal = updatedCart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    setSubTotal(newSubTotal);
    setTotal(newSubTotal + deliveryFee);
  };

  const handlePromoSubmit = (e) => {
    e.preventDefault();
    // Add promo code logic here
    alert('Promo code applied!');
  };

  return (
    <div className="checkout-page">
      {/* Header */}
      <header className="site-header">
        <div className="header-content">
          <Link to="/" className="logo">Tomato.</Link>
          <nav className="main-nav">
            <Link to="/" className="active">home</Link>
            <Link to="/menu">menu</Link>
            <Link to="/mobile-app">mobile-app</Link>
            <Link to="/contact">contact us</Link>
          </nav>
          <div className="header-actions">
            <button className="search-btn">
              <i className="fas fa-search"></i>
            </button>
            <Link to="/cart" className="cart-btn">
              <i className="fas fa-shopping-basket"></i>
            </Link>
            <Link to="/signin" className="sign-in-btn">sign in</Link>
          </div>
        </div>
      </header>

      <div className="checkout-container">
        {/* Cart Table */}
        <table className="cart-table">
          <thead>
            <tr>
              <th>Items</th>
              <th>Title</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Total</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>
            {cart.map(item => (
              <tr key={item.id}>
                <td>
                  <img src={item.image} alt={item.name} className="item-image" />
                </td>
                <td>{item.name}</td>
                <td>${item.price.toFixed(2)}</td>
                <td>
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                    min="1"
                  />
                </td>
                <td>${(item.price * item.quantity).toFixed(2)}</td>
                <td>
                  <button onClick={() => handleRemoveItem(item.id)} className="remove-btn">
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="checkout-summary">
          {/* Cart Total */}
          <div className="cart-total">
            <h2>Cart Total</h2>
            <div className="total-row">
              <span>Subtotal</span>
              <span>${subTotal.toFixed(2)}</span>
            </div>
            <div className="total-row">
              <span>Delivery Fee</span>
              <span>${deliveryFee.toFixed(2)}</span>
            </div>
            <div className="total-row final-total">
              <span>Total</span>
              <span>${total.toFixed(2)}</span>
            </div>
            <button className="proceed-btn">PROCEED TO CHECKOUT</button>
          </div>

          {/* Promo Code */}
          <div className="promo-section">
            <p>If you have a promo code, enter it here</p>
            <form className="promo-form" onSubmit={handlePromoSubmit}>
              <input
                type="text"
                placeholder="Promo Code"
                value={promoCode}
                onChange={(e) => setPromoCode(e.target.value)}
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
