import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext.js';
import { useCart } from '../context/CartContext.js';
import { FiHome, FiShoppingBag, FiUser, FiShoppingCart, FiLogOut, FiMenu, FiCoffee, FiPackage } from 'react-icons/fi';
import LocationSelector from './LocationSelector.js';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const { getCartItemCount } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [userLocation, setUserLocation] = useState(null);
  const [activeTab, setActiveTab] = useState('');

  // Load saved location on component mount
  useEffect(() => {
    const savedLocation = localStorage.getItem('userLocation');
    const savedCoordinates = localStorage.getItem('userCoordinates');

    if (savedLocation) {
      setUserLocation({
        address: savedLocation,
        coordinates: savedCoordinates ? JSON.parse(savedCoordinates) : null
      });
    }
  }, []);

  // Set active tab based on current route
  useEffect(() => {
    if (location.pathname.includes('order-online')) {
      setActiveTab('order-online');
    } else if (location.pathname.includes('dine-out')) {
      setActiveTab('dine-out');
    } else if (location.pathname === '/') {
      setActiveTab('home');
    } else {
      setActiveTab('');
    }
  }, [location.pathname]);

  const handleLocationSelect = (locationData) => {
    setUserLocation(locationData);
    // You could also dispatch this to a global state/context if needed
    console.log('Location selected:', locationData);
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const toggleMenu = (e) => {
    e.stopPropagation();
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Close menus when clicking outside
  useEffect(() => {
    const closeMenus = () => {
      setIsMenuOpen(false);
    };

    document.addEventListener('click', closeMenus);
    return () => document.removeEventListener('click', closeMenus);
  }, []);

  // Close mobile menu when changing routes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location.pathname]);

  return (
    <nav className="navbar">
      <div className="nav-content">
        <div className="nav-left">
          <Link to="/" className="nav-logo">
            FoodDelivery
          </Link>
          <LocationSelector onSelectLocation={handleLocationSelect} />
        </div>

        <div className="main-navigation">
          <Link to="/restaurants/order-online" className={`main-nav-link ${activeTab === 'order-online' ? 'active' : ''}`}>
            <FiPackage />
            <span>Order Online</span>
          </Link>

          <Link to="/restaurants/dine-out" className={`main-nav-link ${activeTab === 'dine-out' ? 'active' : ''}`}>
            <FiCoffee />
            <span>Dine Out</span>
          </Link>
        </div>

        <div className="nav-right">
          <Link to="/cart" className="cart-link">
            <FiShoppingCart className="nav-icon" />
            {getCartItemCount() > 0 && (
              <span className="cart-count">{getCartItemCount()}</span>
            )}
          </Link>

          {user ? (
            <div className="user-menu">
              <button onClick={toggleMenu} className="user-menu-btn">
                <FiUser className="nav-icon" />
                <span>{user.name?.split(' ')[0] || 'Account'}</span>
              </button>
              {isMenuOpen && (
                <div className="dropdown-menu">
                  <Link to="/profile" className="dropdown-item">
                    <FiUser /> Profile
                  </Link>
                  <Link to="/orders" className="dropdown-item">
                    <FiShoppingBag /> Orders
                  </Link>
                  <button onClick={handleLogout} className="dropdown-item">
                    <FiLogOut /> Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="login-btn">Login</Link>
              <Link to="/register" className="register-btn">Register</Link>
            </div>
          )}

          <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
            <FiMenu />
          </button>
        </div>

        <div className={`mobile-nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
          <Link to="/" className={`nav-link ${activeTab === 'home' ? 'active' : ''}`}>
            <FiHome className="nav-icon" /> Home
          </Link>

          <Link to="/restaurants/order-online" className={`nav-link ${activeTab === 'order-online' ? 'active' : ''}`}>
            <FiPackage className="nav-icon" /> Order Online
          </Link>

          <Link to="/restaurants/dine-out" className={`nav-link ${activeTab === 'dine-out' ? 'active' : ''}`}>
            <FiCoffee className="nav-icon" /> Dine Out
          </Link>

          <Link to="/cart" className="nav-link">
            <FiShoppingCart className="nav-icon" /> Cart 
            {getCartItemCount() > 0 && (
              <span className="mobile-cart-count">{getCartItemCount()}</span>
            )}
          </Link>

          {!user && (
            <>
              <Link to="/login" className="nav-link">
                <FiUser className="nav-icon" /> Login
              </Link>
              <Link to="/register" className="nav-link register-link">
                Register
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
