import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiUser, FiMail, FiPhone, FiMapPin, FiEdit, FiSave, FiX } from 'react-icons/fi';
import { useAuth } from '../context/AuthContext.js';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const { user, updateProfile, fetchProfile, logout } = useAuth();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [profileData, setProfileData] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });

  // Fetch profile data
  useEffect(() => {
    const getProfileData = async () => {
      if (!user) {
        navigate('/login');
        return;
      }

      setLoading(true);
      const result = await fetchProfile();
      
      if (result.success) {
        setProfileData(result.data);
        setFormData({
          name: result.data.name || '',
          email: result.data.email || '',
          phone: result.data.phone || '',
          address: result.data.address || ''
        });
      } else {
        setError("Failed to load profile. Please try again.");
      }
      
      setLoading(false);
    };

    getProfileData();
  }, [user, navigate, fetchProfile]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleEditToggle = () => {
    if (isEditing) {
      // Cancel editing - reset form data
      setFormData({
        name: profileData.name || '',
        email: profileData.email || '',
        phone: profileData.phone || '',
        address: profileData.address || ''
      });
    }
    setIsEditing(!isEditing);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError(null);

    try {
      const result = await updateProfile(formData);
      
      if (result.success) {
        setProfileData({
          ...profileData,
          ...formData
        });
        setIsEditing(false);
      } else {
        setError("Failed to update profile. Please try again.");
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    } finally {
      setSaving(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="profile-container">
          <div className="profile-loading">
            <div className="loading-spinner"></div>
            <p>Loading profile...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!profileData) {
    return (
      <div className="profile-page">
        <div className="profile-container">
          <div className="profile-error">
            <p>{error || "Failed to load profile. Please try again."}</p>
            <button 
              className="primary-button" 
              onClick={() => navigate('/login')}
            >
              Go to Login
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-container">
        <div className="profile-header">
          <h1>My Profile</h1>
          <button 
            className={`edit-button ${isEditing ? 'cancel' : ''}`}
            onClick={handleEditToggle}
          >
            {isEditing ? (
              <>
                <FiX /> Cancel
              </>
            ) : (
              <>
                <FiEdit /> Edit
              </>
            )}
          </button>
        </div>

        {error && (
          <div className="profile-error-message">
            <p>{error}</p>
          </div>
        )}

        <div className="profile-avatar-section">
          <div className="profile-avatar">
            {profileData.photo ? (
              <img src={profileData.photo} alt={profileData.name} />
            ) : (
              <div className="profile-avatar-placeholder">
                <FiUser />
              </div>
            )}
          </div>
          {isEditing && (
            <button className="avatar-upload-button">
              Change Photo
            </button>
          )}
        </div>

        <form 
          className="profile-form"
          onSubmit={handleSubmit}
        >
          <div className="form-group">
            <label>
              <FiUser className="input-icon" />
              <span>Full Name</span>
            </label>
            {isEditing ? (
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            ) : (
              <p className="profile-field">{profileData.name}</p>
            )}
          </div>

          <div className="form-group">
            <label>
              <FiMail className="input-icon" />
              <span>Email</span>
            </label>
            {isEditing ? (
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                disabled
              />
            ) : (
              <p className="profile-field">{profileData.email}</p>
            )}
          </div>

          <div className="form-group">
            <label>
              <FiPhone className="input-icon" />
              <span>Phone Number</span>
            </label>
            {isEditing ? (
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                placeholder="Enter your phone number"
              />
            ) : (
              <p className="profile-field">
                {profileData.phone || "Not provided"}
              </p>
            )}
          </div>

          <div className="form-group">
            <label>
              <FiMapPin className="input-icon" />
              <span>Address</span>
            </label>
            {isEditing ? (
              <textarea
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                placeholder="Enter your delivery address"
                rows="3"
              ></textarea>
            ) : (
              <p className="profile-field">
                {profileData.address || "Not provided"}
              </p>
            )}
          </div>

          {isEditing && (
            <button 
              type="submit" 
              className="save-button"
              disabled={saving}
            >
              {saving ? (
                <>Saving...</>
              ) : (
                <>
                  <FiSave /> Save Changes
                </>
              )}
            </button>
          )}
        </form>

        <div className="profile-actions">
          <button 
            className="logout-button"
            onClick={handleLogout}
          >
            Log Out
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
