import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { FiArrowLeft, FiPhone, FiClock, FiMapPin, FiCheck } from "react-icons/fi";
import "./OrderTracking.css";

const OrderTracking = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentStep, setCurrentStep] = useState(2); // Default to "In Delivery" for demo

  // Mock data - in a real app, this would come from an API
  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setOrder({
        id: orderId || "ORD12345",
        restaurant: "Pizza Palace",
        items: [
          { id: 1, name: "Margherita Pizza", quantity: 1, price: 199 },
          { id: 2, name: "Garlic Bread", quantity: 1, price: 99 }
        ],
        total: 298,
        status: "In Delivery",
        estimatedDelivery: "15 minutes",
        deliveryAddress: "42, Gopal Krishna Society, Athwa, Surat",
        placedAt: "2023-04-12 19:30",
        currentLocation: "Near Athwa Gate",
        deliveryPerson: {
          name: "Raj Kumar",
          phone: "+91 98765 43210",
          photo: "https://randomuser.me/api/portraits/men/22.jpg"
        }
      });
      setLoading(false);
    }, 1000);
  }, [orderId]);

  // Simulate progress updates
  useEffect(() => {
    if (!loading && order) {
      const interval = setInterval(() => {
        if (currentStep < 4) {
          // Update current step periodically to simulate progress
          const randomProgress = Math.random();
          if (randomProgress > 0.7) {
            setCurrentStep(prev => Math.min(prev + 1, 4));
          }
        } else {
          clearInterval(interval);
        }
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [loading, order, currentStep]);

  const getStatusText = (step) => {
    switch(step) {
      case 1: return "Order Confirmed";
      case 2: return "Preparing Food";
      case 3: return "In Delivery";
      case 4: return "Delivered";
      default: return "Processing";
    }
  };

  if (loading) {
    return (
      <div className="order-tracking-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading order details...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="order-tracking-page">
      <div className="tracking-container">
        <div className="tracking-header">
          <Link to="/orders" className="back-button">
            <FiArrowLeft />
          </Link>
          <h1>Order Tracking</h1>
        </div>

        <div className="order-info">
          <h2>{order.restaurant}</h2>
          <p className="order-id">Order ID: {order.id}</p>
          <p className="order-time">
            <FiClock className="icon" /> Placed at {order.placedAt}
          </p>
        </div>

        <div className="tracking-status">
          <h3>Current Status: {getStatusText(currentStep)}</h3>
          <div className="progress-container">
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${(currentStep / 4) * 100}%` }}
              ></div>
            </div>
            <div className="progress-steps">
              <div className={`progress-step ${currentStep >= 1 ? "active" : ""}`}>
                <div className="step-icon">
                  {currentStep > 1 ? <FiCheck /> : 1}
                </div>
                <span>Confirmed</span>
              </div>
              <div className={`progress-step ${currentStep >= 2 ? "active" : ""}`}>
                <div className="step-icon">
                  {currentStep > 2 ? <FiCheck /> : 2}
                </div>
                <span>Preparing</span>
              </div>
              <div className={`progress-step ${currentStep >= 3 ? "active" : ""}`}>
                <div className="step-icon">
                  {currentStep > 3 ? <FiCheck /> : 3}
                </div>
                <span>In Delivery</span>
              </div>
              <div className={`progress-step ${currentStep >= 4 ? "active" : ""}`}>
                <div className="step-icon">
                  {currentStep >= 4 ? <FiCheck /> : 4}
                </div>
                <span>Delivered</span>
              </div>
            </div>
          </div>
          
          <div className="estimated-time">
            <p>
              <FiClock className="icon" />
              Estimated Delivery: <strong>{order.estimatedDelivery}</strong>
            </p>
            <p>
              <FiMapPin className="icon" />
              Current Location: <strong>{order.currentLocation}</strong>
            </p>
          </div>
        </div>

        {currentStep === 3 && (
          <div className="delivery-person">
            <h3>Delivery Partner</h3>
            <div className="delivery-person-details">
              <div className="delivery-person-photo">
                <img src={order.deliveryPerson.photo} alt={order.deliveryPerson.name} />
              </div>
              <div className="delivery-person-info">
                <p className="delivery-person-name">{order.deliveryPerson.name}</p>
                <a href={`tel:${order.deliveryPerson.phone}`} className="call-button">
                  <FiPhone /> Call
                </a>
              </div>
            </div>
          </div>
        )}

        <div className="delivery-address">
          <h3>Delivery Address</h3>
          <p>
            <FiMapPin className="icon" /> {order.deliveryAddress}
          </p>
        </div>

        <div className="order-summary">
          <h3>Order Summary</h3>
          <div className="order-items">
            {order.items.map(item => (
              <div key={item.id} className="order-item">
                <div className="item-info">
                  <p className="item-name">{item.name}</p>
                  <p className="item-quantity">x{item.quantity}</p>
                </div>
                <p className="item-price">₹{item.price}</p>
              </div>
            ))}
          </div>
          <div className="order-total">
            <p>Total</p>
            <p>₹{order.total}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderTracking;
