import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext.js';
import { FiUser, FiMail, FiPhone, FiHome, FiMapPin, FiCalendar, FiEdit, FiArrowLeft } from 'react-icons/fi';
import './Profile.css';
import API_BASE_URL from '../config/api.js';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const [isEditMode, setIsEditMode] = useState(false);
  const [activeTab, setActiveTab] = useState('personal');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    street: '',
    city: '',
    state: '',
    zipcode: '',
    country: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // Fetch current profile data on component mount
    const fetchProfileData = async () => {
      try {
        const res = await fetch('http://localhost:5001/api/auth/me', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${user.token}`,
          },
        });

        if (!res.ok) throw new Error('Failed to fetch profile data');
        const data = await res.json();

        setFormData({
          name: data.name || '',
          email: data.email || '',
          phone: data.phone || '',
          street: data.address?.street || '',
          city: data.address?.city || '',
          state: data.address?.state || '',
          zipcode: data.address?.zipcode || '',
          country: data.address?.country || ''
        });
      } catch (err) {
        setError('Failed to fetch profile data.');
        console.error('Profile fetch error:', err);
      }
    };

    if (user) {
      fetchProfileData();
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      let updatedUser;

      // Handling Profile Update (Personal Info)
      if (activeTab === 'personal') {
        const profileData = {
          name: formData.name,
          phone: formData.phone,
        };

        const res = await fetch('http://localhost:5001/api/auth/update-profile', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify(profileData),
        });

        if (!res.ok) throw new Error('Failed to update profile');
        const data = await res.json();
        updatedUser = updateUser(data);
      }

      // Handling Address Update
      if (activeTab === 'address') {
        const addressData = {
          street: formData.street,
          city: formData.city,
          state: formData.state,
          zipcode: formData.zipcode,
          country: formData.country,
        };

        const res = await fetch('http://localhost:5001/api/auth/update-address', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify(addressData),
        });

        if (!res.ok) throw new Error('Failed to update address');
        const data = await res.json();
        updatedUser = updateUser(data);
      }

      setSuccess('Profile updated successfully!');
      setIsEditMode(false);
    } catch (err) {
      setError('Failed to update profile. Please try again.');
      console.error('Profile update error:', err);
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="loading-container">
        <div className="loading">Please log in to view your profile</div>
      </div>
    );
  }

  const formatAddress = () => {
    const { street, city, state, zipcode, country } = formData;
    const addressParts = [];
    if (street) addressParts.push(street);
    if (city) addressParts.push(city);
    if (state) addressParts.push(state);
    if (zipcode) addressParts.push(zipcode);
    if (country) addressParts.push(country);
    return addressParts.join(', ') || 'No address provided';
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <h1>My Profile</h1>
        <p>Manage your account information and preferences</p>
      </div>
      <div className="profile-content">
        <div className="profile-sidebar">
          <div className="profile-avatar-container">
            <div className="profile-avatar-large">
              {user.photo ? (
                <img src={user.photo} alt={user.name} />
              ) : (
                <div className="profile-initial-large">{user.name.charAt(0).toUpperCase()}</div>
              )}
            </div>
            <button className="edit-avatar-btn">
              <FiEdit />
            </button>
          </div>
          <div className="profile-name-card">
            <h2>{user.name}</h2>
            <p>{user.email}</p>
            {user.phone && <p><FiPhone /> {user.phone}</p>}
          </div>
          <div className="profile-tabs">
            <button 
              className={`profile-tab ${activeTab === 'personal' ? 'active' : ''}`}
              onClick={() => setActiveTab('personal')}
            >
              <FiUser className="tab-icon" />
              <span>Personal Info</span>
            </button>
            <button 
              className={`profile-tab ${activeTab === 'address' ? 'active' : ''}`}
              onClick={() => setActiveTab('address')}
            >
              <FiMapPin className="tab-icon" />
              <span>Address</span>
            </button>
          </div>
        </div>
        <div className="profile-main">
          {isEditMode ? (
            <div className="edit-profile-form">
              <div className="edit-header">
                <h2>Edit {activeTab === 'personal' ? 'Profile' : 'Address'}</h2>
                <button 
                  className="back-button" 
                  onClick={() => setIsEditMode(false)}
                >
                  <FiArrowLeft /> Back
                </button>
              </div>
              {error && <div className="error-message">{error}</div>}
              {success && <div className="success-message">{success}</div>}
              <form onSubmit={handleSubmit}>
                {activeTab === 'personal' && (
                  <>
                    <div className="form-group">
                      <label htmlFor="name">Full Name</label>
                      <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="email">Email</label>
                      <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        disabled
                      />
                      <small>Email cannot be changed</small>
                    </div>
                    <div className="form-group">
                      <label htmlFor="mobile">Mobile Number</label>
                      <input
                        type="tel"
                        id="phone"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        placeholder="Enter your mobile number"
                      />
                    </div>
                  </>
                )}
                {activeTab === 'address' && (
                  <>
                    <div className="form-group">
                      <label htmlFor="street">Street Address</label>
                      <input
                        type="text"
                        id="street"
                        name="street"
                        value={formData.street}
                        onChange={handleInputChange}
                        placeholder="Enter street address"
                      />
                    </div>
                    <div className="form-row">
                      <div className="form-group">
                        <label htmlFor="city">City</label>
                        <input
                          type="text"
                          id="city"
                          name="city"
                          value={formData.city}
                          onChange={handleInputChange}
                          placeholder="Enter city"
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="state">State</label>
                        <input
                          type="text"
                          id="state"
                          name="state"
                          value={formData.state}
                          onChange={handleInputChange}
                          placeholder="Enter state"
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <div className="form-group">
                        <label htmlFor="zipcode">ZIP Code</label>
                        <input
                          type="text"
                          id="zipcode"
                          name="zipcode"
                          value={formData.zipcode}
                          onChange={handleInputChange}
                          placeholder="Enter ZIP code"
                        />
                      </div>
                      <div className="form-group">
                        <label htmlFor="country">Country</label>
                        <input
                          type="text"
                          id="country"
                          name="country"
                          value={formData.country}
                          onChange={handleInputChange}
                          placeholder="Enter country"
                        />
                      </div>
                    </div>
                  </>
                )}
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-btn"
                    onClick={() => setIsEditMode(false)}
                  >
                    Cancel
                  </button>
                  <button type="submit" className="save-btn" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Changes'}
                  </button>
                </div>
              </form>
            </div>
          ) : (
            <div className="profile-summary">
              {activeTab === 'personal' && (
                <div className="personal-info">
                  <p><strong>Name:</strong> {formData.name}</p>
                  <p><strong>Email:</strong> {formData.email}</p>
                  <p><strong>Phone:</strong> {formData.phone}</p>
                </div>
              )}
              {activeTab === 'address' && (
                <div className="address-info">
                  <p><strong>Address:</strong> {formatAddress()}</p>
                </div>
              )}
              <button 
                className="edit-button"
                onClick={() => setIsEditMode(true)}
              >
                Edit
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
