import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiMail, FiLock } from 'react-icons/fi';
import { FcGoogle } from 'react-icons/fc';
import { useAuth } from '../context/AuthContext.js';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { login, error: authError, isAuthenticated } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Redirect if already logged in
  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || '/';
      navigate(from);
    }
  }, [isAuthenticated, navigate, location]);

  // Update local error state when auth context error changes
  useEffect(() => {
    if (authError) {
      setError(authError);
      // Clear password field on error
      setFormData(prev => ({
        ...prev,
        password: ''
      }));
    }
  }, [authError]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const resetForm = () => {
    setFormData({
      email: '',
      password: ''
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      if (!formData.email || !formData.password) {
        throw new Error('All fields are required');
      }

      await login(formData.email, formData.password);
      // On successful login, the useEffect will handle navigation
      // Clear form data after login attempt (successful or not)
      resetForm();
    } catch (error) {
      setError(error.message || 'Login failed');
      // Clear only password on error
      setFormData(prev => ({
        ...prev,
        password: ''
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    alert('Google login is not implemented yet.');
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-header">
          <h1>Welcome Back</h1>
          <p>Sign in to continue your food journey</p>
        </div>

        {error && (
          <div className="error-message">
            {error}
            <button className="clear-error-btn" onClick={() => setError('')}>×</button>
          </div>
        )}

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <div className="input-wrapper">
              <FiMail className="icon" />
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                required
                disabled={loading}
              />
              {formData.email && (
                <button 
                  type="button"
                  className="clear-input-btn"
                  onClick={() => setFormData(prev => ({ ...prev, email: '' }))}
                >
                  ×
                </button>
              )}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-wrapper">
              <FiLock className="icon" />
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
                required
                disabled={loading}
              />
              {formData.password && (
                <button 
                  type="button"
                  className="clear-input-btn"
                  onClick={() => setFormData(prev => ({ ...prev, password: '' }))}
                >
                  ×
                </button>
              )}
            </div>
          </div>

          <div className="form-actions">
            <button 
              type="submit" 
              className="login-btn"
              disabled={loading}
            >
              {loading ? 'Signing in...' : 'Sign In'}
            </button>

            <button 
              type="button" 
              className="reset-form-btn" 
              onClick={resetForm}
              disabled={loading || (!formData.email && !formData.password)}
            >
              Clear Form
            </button>
          </div>
        </form>

        <div className="social-login">
          <p>Or continue with</p>
          <div className="social-buttons">
            <button 
              className="social-btn google-btn" 
              onClick={handleGoogleLogin}
              type="button"
            >
              <FcGoogle /> Google
            </button>
          </div>
        </div>

        <div className="register-link">
          Don't have an account? <Link to="/register">Register here</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
