import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiMail, FiLock, FiUser, FiPhone, FiMapPin, FiHome, FiAlertCircle } from 'react-icons/fi';
import { FcGoogle } from 'react-icons/fc';
import { FaGithub } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext.js';
import './Register.css';

const Register = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { register, error: authError, isAuthenticated } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    password: '',
    confirmPassword: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipcode: '',
      country: ''
    }
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [registerSuccess, setRegisterSuccess] = useState(false);

  // Redirect if already logged in
  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || '/';
      navigate(from);
    }
  }, [isAuthenticated, navigate, location]);

  // Update local error state when auth context error changes
  useEffect(() => {
    if (authError) {
      setError(authError);
    }
  }, [authError]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      // Handle nested address fields
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    if (error) setError('');
  };

  const resetForm = () => {
    setFormData({
      name: '',
      email: '',
      mobile: '',
      password: '',
      confirmPassword: '',
      address: {
        street: '',
        city: '',
        state: '',
        zipcode: '',
        country: ''
      }
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    setRegisterSuccess(false);

    try {
      // Validate input
      if (!formData.name || !formData.email || !formData.password || !formData.confirmPassword) {
        throw new Error('All required fields must be filled');
      }

      if (formData.password !== formData.confirmPassword) {
        throw new Error('Passwords do not match');
      }

      // Validate mobile number if provided
      if (formData.mobile) {
        const mobileRegex = /^[0-9]{10}$/;
        if (!mobileRegex.test(formData.mobile)) {
          throw new Error('Please enter a valid 10-digit mobile number');
        }
      }

      // Validate zipcode if provided
      if (formData.address.zipcode) {
        const zipcodeRegex = /^[0-9]{5,6}$/;
        if (!zipcodeRegex.test(formData.address.zipcode)) {
          throw new Error('Please enter a valid zipcode');
        }
      }

      // Call the register function from AuthContext
      await register({
        name: formData.name,
        email: formData.email,
        mobile: formData.mobile,
        password: formData.password,
        address: formData.address
      });

      // Set success state
      setRegisterSuccess(true);

      // Clear form on success
      resetForm();

      // Navigation is handled by the useEffect hook watching isAuthenticated
    } catch (error) {
      console.error('Registration error:', error);

      // User-friendly error messages
      if (error.message.includes('network') || error.message.includes('ERR_CONNECTION_REFUSED')) {
        setError('Could not connect to the server. Using offline mode if possible.');
      } else if (error.message.includes('Email already exists')) {
        setError('This email address is already registered. Please use a different email.');
      } else {
        setError(error.message || 'Registration failed. Please try again.');
      }

      // Clear passwords on error
      setFormData(prev => ({
        ...prev,
        password: '',
        confirmPassword: ''
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignup = () => {
    alert('Google signup is not implemented yet.');
  };

  const handleGithubSignup = () => {
    alert('GitHub signup is not implemented yet.');
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-header">
          <h1>Create Account</h1>
          <p>Join us for a delicious food journey</p>
        </div>

        {registerSuccess && (
          <div className="success-message">
            Account created successfully! Redirecting...
          </div>
        )}

        {error && (
          <div className="error-message">
            <FiAlertCircle className="error-icon" />
            {error}
            <button className="clear-error-btn" onClick={() => setError('')}>×</button>
          </div>
        )}

        <form onSubmit={handleSubmit} className="register-form">
          <div className="form-section">
            <h2>Basic Information</h2>
            <div className="form-group">
              <label htmlFor="name">Full Name *</label>
              <div className="input-wrapper">
                <FiUser className="icon" />
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Enter your full name"
                  required
                  disabled={loading}
                />
                {formData.name && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ ...prev, name: '' }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="email">Email Address *</label>
              <div className="input-wrapper">
                <FiMail className="icon" />
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  required
                  disabled={loading}
                />
                {formData.email && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ ...prev, email: '' }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="mobile">Mobile Number</label>
              <div className="input-wrapper">
                <FiPhone className="icon" />
                <input
                  type="tel"
                  id="mobile"
                  name="mobile"
                  value={formData.mobile}
                  onChange={handleChange}
                  placeholder="Enter your mobile number"
                  disabled={loading}
                />
                {formData.mobile && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ ...prev, mobile: '' }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="password">Password *</label>
              <div className="input-wrapper">
                <FiLock className="icon" />
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Create a password"
                  required
                  disabled={loading}
                />
                {formData.password && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ ...prev, password: '' }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password *</label>
              <div className="input-wrapper">
                <FiLock className="icon" />
                <input
                  type="password"
                  id="confirmPassword"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  placeholder="Confirm your password"
                  required
                  disabled={loading}
                />
                {formData.confirmPassword && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ ...prev, confirmPassword: '' }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>
          </div>

          <div className="form-section">
            <h2>Delivery Information</h2>
            <div className="form-group">
              <label htmlFor="street">Street Address</label>
              <div className="input-wrapper">
                <FiHome className="icon" />
                <input
                  type="text"
                  id="street"
                  name="address.street"
                  value={formData.address.street}
                  onChange={handleChange}
                  placeholder="Enter your street address"
                  disabled={loading}
                />
                {formData.address.street && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      address: { ...prev.address, street: '' } 
                    }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="city">City</label>
              <div className="input-wrapper">
                <FiMapPin className="icon" />
                <input
                  type="text"
                  id="city"
                  name="address.city"
                  value={formData.address.city}
                  onChange={handleChange}
                  placeholder="Enter your city"
                  disabled={loading}
                />
                {formData.address.city && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      address: { ...prev.address, city: '' } 
                    }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="state">State</label>
              <div className="input-wrapper">
                <FiMapPin className="icon" />
                <input
                  type="text"
                  id="state"
                  name="address.state"
                  value={formData.address.state}
                  onChange={handleChange}
                  placeholder="Enter your state"
                  disabled={loading}
                />
                {formData.address.state && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      address: { ...prev.address, state: '' } 
                    }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="zipcode">Zipcode</label>
              <div className="input-wrapper">
                <FiMapPin className="icon" />
                <input
                  type="text"
                  id="zipcode"
                  name="address.zipcode"
                  value={formData.address.zipcode}
                  onChange={handleChange}
                  placeholder="Enter your zipcode"
                  disabled={loading}
                />
                {formData.address.zipcode && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      address: { ...prev.address, zipcode: '' } 
                    }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="country">Country</label>
              <div className="input-wrapper">
                <FiMapPin className="icon" />
                <input
                  type="text"
                  id="country"
                  name="address.country"
                  value={formData.address.country}
                  onChange={handleChange}
                  placeholder="Enter your country"
                  disabled={loading}
                />
                {formData.address.country && (
                  <button 
                    type="button"
                    className="clear-input-btn"
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      address: { ...prev.address, country: '' } 
                    }))}
                  >
                    ×
                  </button>
                )}
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button 
              type="submit" 
              className="register-btn"
              disabled={loading}
            >
              {loading ? 'Creating Account...' : 'Create Account'}
            </button>

            <button 
              type="button" 
              className="reset-form-btn" 
              onClick={resetForm}
              disabled={loading || (
                !formData.name && 
                !formData.email && 
                !formData.mobile && 
                !formData.password && 
                !formData.confirmPassword &&
                !formData.address.street &&
                !formData.address.city &&
                !formData.address.state &&
                !formData.address.zipcode &&
                !formData.address.country
              )}
            >
              Clear Form
            </button>
          </div>

          <div className="social-login">
            <div className="divider">
              <span>Or continue with</span>
            </div>
            <div className="social-buttons">
              <button onClick={handleGoogleSignup}>
                <FcGoogle size={20} />
              </button>
              <button onClick={handleGithubSignup}>
                <FaGithub size={20} />
              </button>
            </div>
          </div>
        </form>

        <div className="login-link">
          Already have an account? <Link to="/login">Sign in here</Link>
        </div>
      </div>
    </div>
  );
};

export default Register;
