import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiUser, FiShoppingBag, FiLogOut, FiHeart, FiPackage, FiClock } from 'react-icons/fi';
import { useAuth } from '../context/AuthContext.js';
import { useOrders } from '../context/OrderContext.js';
import './ProfileIcon.css';

const ProfileIcon = () => {
  const { user, logout, fetchProfile } = useAuth();
  const { activeOrder } = useOrders();
  const [isOpen, setIsOpen] = useState(false);
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(false);
  const dropdownRef = useRef(null);

  // Fetch profile data when component mounts
  useEffect(() => {
    const getProfileData = async () => {
      setLoading(true);
      const result = await fetchProfile();
      if (result.success) {
        setProfileData(result.data);
      }
      setLoading(false);
    };

    if (user) {
      getProfileData();
    }
  }, [user, fetchProfile]);

  const toggleDropdown = (e) => {
    e.stopPropagation();
    setIsOpen(!isOpen);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Get first letter of user name for avatar
  const getInitial = () => {
    if (user && user.name) {
      return user.name.charAt(0).toUpperCase();
    }
    return 'U';
  };

  // Format user name to show in all caps like in the image
  const getFormattedName = () => {
    if (user && user.name) {
      return user.name.toUpperCase();
    }
    return 'USER';
  };

  const handleLogout = () => {
    setIsOpen(false);
    logout();
  };

  if (!user) return null;

  return (
    <div className="profile-icon-container" ref={dropdownRef}>
      <button
        className="profile-icon-button"
        onClick={toggleDropdown}
        aria-label="User menu"
      >
        <div className="profile-avatar">
          {user.photo ? (
            <img src={user.photo} alt={user.name} />
          ) : (
            <div className="profile-initial">{getInitial()}</div>
          )}
        </div>
      </button>

      {isOpen && (
        <div className="profile-dropdown">
          <div className="profile-header">
            <div className="profile-info">
              <span className="profile-name">{getFormattedName()}</span>
              <span className="profile-email">{user.email}</span>
            </div>
          </div>

          {activeOrder && (
            <div className="active-order-section">
              <h3>Active Order</h3>
              <Link 
                to={`/order-tracking/${activeOrder.id}`} 
                className="active-order-item"
                onClick={() => setIsOpen(false)}
              >
                <div className="order-icon-container">
                  <FiPackage className="order-icon" />
                </div>
                <div className="order-details">
                  <span className="order-restaurant">{activeOrder.restaurant.name}</span>
                  <div className="order-status">
                    <span className="status-text">
                      {activeOrder.status === 'in_delivery' ? 'In Delivery' : 
                       activeOrder.status === 'preparing' ? 'Preparing' : 'Confirmed'}
                    </span>
                    {activeOrder.estimatedDelivery && (
                      <div className="status-time">
                        <FiClock className="time-icon" />
                        <span>{activeOrder.estimatedDelivery}</span>
                      </div>
                    )}
                  </div>
                </div>
              </Link>
            </div>
          )}
          
          <div className="profile-menu">
            <Link to="/profile" className="profile-menu-item" onClick={() => setIsOpen(false)}>
              <FiUser className="menu-icon" />
              <span>My Profile</span>
            </Link>
            <Link to="/orders" className="profile-menu-item" onClick={() => setIsOpen(false)}>
              <FiShoppingBag className="menu-icon" />
              <span>My Orders</span>
            </Link>
            <Link to="/favorites" className="profile-menu-item" onClick={() => setIsOpen(false)}>
              <FiHeart className="menu-icon" />
              <span>Favorites</span>
            </Link>
            <button className="profile-menu-item logout-button" onClick={handleLogout}>
              <FiLogOut className="menu-icon" />
              <span>Logout</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfileIcon;
