import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext.js';
import { useAuth } from '../context/AuthContext.js';
import { useSearch } from '../context/SearchContext.js';
import './Home.css';

// Mock data for testing
const mockCategories = [
  { id: 1, name: 'Pizza', icon: 'üçï' },
  { id: 2, name: 'Burger', icon: 'üçî' },
  { id: 3, name: 'Sushi', icon: 'üç£' },
  { id: 4, name: 'Chinese', icon: 'ü•°' },
  { id: 5, name: 'Indian', icon: 'üçõ' },
  { id: 6, name: 'Mexican', icon: 'üåÆ' },
  { id: 7, name: 'Italian', icon: 'üçù' },
  { id: 8, name: 'Desserts', icon: 'üç∞' }
];

const mockRestaurants = [
  {
    _id: '1',
    name: 'Pizza Palace',
    cuisine: 'Italian',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.5',
    deliveryTime: '30-45 min',
    minOrder: '$10',
    deliveryFee: 'Free'
  },
  {
    _id: '2',
    name: 'Burger King',
    cuisine: 'American',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.2',
    deliveryTime: '20-30 min',
    minOrder: '$5',
    deliveryFee: '$2.99'
  },
  {
    _id: '3',
    name: 'Sushi Master',
    cuisine: 'Japanese',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.8',
    deliveryTime: '25-35 min',
    minOrder: '$15',
    deliveryFee: 'Free'
  }
];

const mockFoodItems = [
  {
    _id: '1',
    name: 'Margherita Pizza',
    description: 'Classic pizza with tomato sauce, mozzarella, and basil',
    price: 12.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '1',
      name: 'Pizza Palace'
    },
    rating: 4.5,
    deliveryTime: '30 min'
  },
  {
    _id: '2',
    name: 'Cheeseburger',
    description: 'Juicy beef patty with cheese, lettuce, and special sauce',
    price: 8.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '2',
      name: 'Burger King'
    },
    rating: 4.2,
    deliveryTime: '20 min'
  },
  {
    _id: '3',
    name: 'California Roll',
    description: 'Fresh crab, avocado, and cucumber wrapped in seaweed',
    price: 10.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '3',
      name: 'Sushi Master'
    },
    rating: 4.8,
    deliveryTime: '25 min'
  }
];

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();
  const { setSearchResults } = useSearch();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [restaurantsRes, foodItemsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants', {
            headers: { 'Content-Type': 'application/json' }
          }).catch(() => ({ data: mockRestaurants })),
          axios.get('http://localhost:5000/api/menu/featured', {
            headers: { 'Content-Type': 'application/json' }
          }).catch(() => ({ data: mockFoodItems }))
        ]);
        
        setRestaurants(restaurantsRes.data);
        setFoodItems(foodItemsRes.data);
        setLoading(false);
      } catch (err) {
        setRestaurants(mockRestaurants);
        setFoodItems(mockFoodItems);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setSearchResults({
        query: searchQuery,
        restaurants: restaurants.filter(r => 
          r.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          r.cuisine.toLowerCase().includes(searchQuery.toLowerCase())
        ),
        items: foodItems.filter(i => 
          i.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          i.description.toLowerCase().includes(searchQuery.toLowerCase())
        )
      });
      navigate('/search');
    }
  };

  const handleAddToCart = (item) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    addToCart(item);
    setNotificationMessage(`${item.name} added to cart!`);
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  const filteredRestaurants = selectedCategory
    ? restaurants.filter(r => r.cuisine.toLowerCase() === mockCategories.find(c => c.id === selectedCategory).name.toLowerCase())
    : restaurants;

  const filteredFoodItems = selectedCategory
    ? foodItems.filter(i => i.restaurant.cuisine.toLowerCase() === mockCategories.find(c => c.id === selectedCategory).name.toLowerCase())
    : foodItems;

  if (loading) return <div className="loading">Loading...</div>;

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Delicious Food Delivered To Your Doorstep</h1>
          <p>Order from your favorite restaurants with just a few clicks</p>
          <form onSubmit={handleSearch} className="search-bar">
            <input 
              type="text" 
              placeholder="Search for restaurants or food..." 
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <h2 className="section-title">Categories</h2>
        <div className="categories-grid">
          {mockCategories.map(category => (
            <div 
              key={category.id} 
              className={`category-card ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(selectedCategory === category.id ? null : category.id)}
            >
              <span className="category-icon">{category.icon}</span>
              <span className="category-name">{category.name}</span>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Items Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2 className="section-title">Featured Items</h2>
          <Link to="/menu" className="view-all">View All</Link>
        </div>
        <div className="food-items-grid">
          {filteredFoodItems.map(item => (
            <div key={item._id} className="food-item-card">
              <img 
                src={item.image} 
                alt={item.name}
                className="food-item-image"
              />
              <div className="food-item-info">
                <h3>{item.name}</h3>
                <p className="food-description">{item.description}</p>
                <div className="food-item-meta">
                  <div className="rating">
                    <span>‚≠ê {item.rating}</span>
                    <span>‚è±Ô∏è {item.deliveryTime}</span>
                  </div>
                  <span className="food-price">${item.price.toFixed(2)}</span>
                </div>
                <div className="restaurant-info">
                  <Link to={`/restaurants/${item.restaurant._id}`} className="restaurant-link">
                    {item.restaurant.name}
                  </Link>
                </div>
                <button 
                  className="add-to-cart-btn"
                  onClick={() => handleAddToCart(item)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">Popular Restaurants</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>
        <div className="restaurants-grid">
          {filteredRestaurants.map(restaurant => (
            <Link to={`/restaurants/${restaurant._id}`} key={restaurant._id} className="restaurant-card">
              <img 
                src={restaurant.image} 
                alt={restaurant.name}
                className="restaurant-image"
              />
              <div className="restaurant-info">
                <h3>{restaurant.name}</h3>
                <p className="cuisine">{restaurant.cuisine}</p>
                <div className="restaurant-meta">
                  <div className="rating">
                    <span>‚≠ê {restaurant.rating}</span>
                    <span>‚è±Ô∏è {restaurant.deliveryTime}</span>
                  </div>
                  <div className="delivery-info">
                    <span>Min: {restaurant.minOrder}</span>
                    <span>Fee: {restaurant.deliveryFee}</span>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </section>

      {/* App Download Section */}
      <section className="app-download-section">
        <div className="app-content">
          <div className="app-info">
            <h2>Download Our App</h2>
            <p>Get exclusive offers and faster delivery with our mobile app</p>
            <div className="app-buttons">
              <button className="app-store-btn">
                <span>Download on the</span>
                <span>App Store</span>
              </button>
              <button className="play-store-btn">
                <span>GET IT ON</span>
                <span>Google Play</span>
              </button>
            </div>
          </div>
          <div className="app-image">
            <img src="https://via.placeholder.com/300x600" alt="App Preview" />
          </div>
        </div>
      </section>

      {/* Notification */}
      {showNotification && (
        <div className="notification">
          {notificationMessage}
        </div>
      )}
    </div>
  );
};

export default Home;
