import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { FiMail, FiLock, FiAlertCircle } from 'react-icons/fi';
import { useAuth } from '../context/AuthContext.js';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { login, error: authError, isAuthenticated } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [loginSuccess, setLoginSuccess] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Redirect if already logged in
  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || '/';
      navigate(from);
    }
  }, [isAuthenticated, navigate, location]);

  // Update local error state when auth context error changes
  useEffect(() => {
    if (authError) {
      // Check for specific errors that should be tied to fields
      if (authError.includes('Invalid password')) {
        setFieldErrors({
          ...fieldErrors,
          password: 'Invalid password'
        });
        // Clear password field
        setFormData(prev => ({
          ...prev,
          password: ''
        }));
      } else if (authError.includes('Email not registered')) {
        setFieldErrors({
          ...fieldErrors,
          email: 'Email not registered'
        });
      } else {
        // General error
        setError(authError);
        // Clear password field on error
        setFormData(prev => ({
          ...prev,
          password: ''
        }));
      }
    }
  }, [authError]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear errors for this field
    if (fieldErrors[name]) {
      setFieldErrors(prev => {
        const newErrors = {...prev};
        delete newErrors[name];
        return newErrors;
      });
    }

    // Clear general error
    if (error) setError('');
  };

  const resetForm = () => {
    setFormData({
      email: '',
      password: ''
    });
    setError('');
    setFieldErrors({});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setFieldErrors({});
    setLoading(true);
    setLoginSuccess(false);

    try {
      // Email validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!formData.email) {
        setFieldErrors(prev => ({...prev, email: 'Email is required'}));
        throw new Error('Email is required');
      } else if (!emailRegex.test(formData.email)) {
        setFieldErrors(prev => ({...prev, email: 'Please enter a valid email address'}));
        throw new Error('Please enter a valid email address');
      }

      // Password validation
      if (!formData.password) {
        setFieldErrors(prev => ({...prev, password: 'Password is required'}));
        throw new Error('Password is required');
      } else if (formData.password.length < 6) {
        setFieldErrors(prev => ({...prev, password: 'Password must be at least 6 characters'}));
        throw new Error('Password must be at least 6 characters long');
      }

      await login(formData.email, formData.password);
      setLoginSuccess(true);

      // Clear form data after successful login
      resetForm();
    } catch (error) {
      console.error('Login error:', error);

      // If no specific field errors were set, set a general error
      if (Object.keys(fieldErrors).length === 0) {
        setError(error.message || 'Login failed. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const getInputClassName = (fieldName) => {
    return fieldErrors[fieldName] ? 'input-group error' : 'input-group';
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <h1>Welcome Back</h1>
            <p>Sign in to continue your food journey</p>
          </div>

          {loginSuccess && (
            <div className="success-message">
              Login successful! Redirecting...
            </div>
          )}

          {error && (
            <div className="error-message">
              <FiAlertCircle className="error-icon" />
              {error}
              <button className="clear-error-btn" onClick={() => setError('')}>×</button>
            </div>
          )}

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <div className={getInputClassName('email')}>
                <FiMail className="input-icon" />
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  disabled={loading}
                  autoComplete="email"
                />
              </div>
              {fieldErrors.email && (
                <div className="field-error">{fieldErrors.email}</div>
              )}
            </div>

            <div className="form-group">
              <div className="label-row">
                <label htmlFor="password">Password</label>
                <Link to="/forgot-password" className="forgot-password">Forgot Password?</Link>
              </div>
              <div className={getInputClassName('password')}>
                <FiLock className="input-icon" />
                <input
                  type={showPassword ? "text" : "password"}
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Enter your password"
                  disabled={loading}
                  autoComplete="current-password"
                />
                <button 
                  type="button" 
                  className="password-toggle"
                  onClick={togglePasswordVisibility}
                >
                  {showPassword ? "Hide" : "Show"}
                </button>
              </div>
              {fieldErrors.password && (
                <div className="field-error">{fieldErrors.password}</div>
              )}
            </div>

            <button 
              type="submit" 
              className="auth-button primary-button"
              disabled={loading}
            >
              {loading ? 'Signing in...' : 'Sign In'}
            </button>
          </form>

          <div className="auth-footer">
            <p>Don't have an account? <Link to="/register" className="auth-link">Create Account</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
