import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    priceRange: 'all',
    rating: 'all',
    deliveryTime: 'all'
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [restaurantsRes, featuredItemsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants'),
          axios.get('http://localhost:5000/api/menu/featured')
        ]);
        setRestaurants(restaurantsRes.data);
        setFeaturedItems(featuredItemsRes.data);
        setError(null);
      } catch (err) {
        setError('Failed to fetch data');
        // Use mock data if API fails
        setRestaurants(mockRestaurants);
        setFeaturedItems(mockFeaturedItems);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const filteredRestaurants = restaurants.filter(restaurant => {
    const matchesSearch = restaurant.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = !selectedCategory || restaurant.cuisine === selectedCategory;
    const matchesPrice = filters.priceRange === 'all' || 
      (filters.priceRange === 'low' && restaurant.priceRange === 'low') ||
      (filters.priceRange === 'medium' && restaurant.priceRange === 'medium') ||
      (filters.priceRange === 'high' && restaurant.priceRange === 'high');
    const matchesRating = filters.rating === 'all' || 
      (filters.rating === '4+' && restaurant.rating >= 4) ||
      (filters.rating === '3+' && restaurant.rating >= 3);
    const matchesDeliveryTime = filters.deliveryTime === 'all' ||
      (filters.deliveryTime === 'fast' && restaurant.deliveryTime <= 30) ||
      (filters.deliveryTime === 'medium' && restaurant.deliveryTime <= 45);

    return matchesSearch && matchesCategory && matchesPrice && matchesRating && matchesDeliveryTime;
  });

  const filteredFeaturedItems = featuredItems.filter(item => {
    const matchesSearch = item.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = !selectedCategory || item.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = [
    { id: 'all', name: 'All', icon: 'üçΩÔ∏è', count: featuredItems.length },
    { id: 'pizza', name: 'Pizza', icon: 'üçï', count: featuredItems.filter(item => item.category === 'pizza').length },
    { id: 'burger', name: 'Burgers', icon: 'üçî', count: featuredItems.filter(item => item.category === 'burger').length },
    { id: 'sushi', name: 'Sushi', icon: 'üç£', count: featuredItems.filter(item => item.category === 'sushi').length },
    { id: 'pasta', name: 'Pasta', icon: 'üçù', count: featuredItems.filter(item => item.category === 'pasta').length },
    { id: 'dessert', name: 'Desserts', icon: 'üç∞', count: featuredItems.filter(item => item.category === 'dessert').length }
  ];

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Delicious Food Delivered To Your Doorstep</h1>
          <p>Order from your favorite restaurants and enjoy the best food in town</p>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search for restaurants or food..."
              value={searchQuery}
              onChange={handleSearch}
            />
            <button>Search</button>
          </div>
          <div className="quick-filters">
            <button 
              className="filter-btn"
              onClick={() => setShowFilters(!showFilters)}
            >
              Filters
            </button>
            {showFilters && (
              <div className="filters-dropdown">
                <div className="filter-group">
                  <label>Price Range</label>
                  <select name="priceRange" value={filters.priceRange} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="low">$</option>
                    <option value="medium">$$</option>
                    <option value="high">$$$</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label>Rating</label>
                  <select name="rating" value={filters.rating} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="4+">4+ Stars</option>
                    <option value="3+">3+ Stars</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label>Delivery Time</label>
                  <select name="deliveryTime" value={filters.deliveryTime} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="fast">Fast (‚â§30 min)</option>
                    <option value="medium">Medium (‚â§45 min)</option>
                  </select>
                </div>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="section-header">
          <h2 className="section-title">Categories</h2>
          <Link to="/categories" className="view-all">View All</Link>
        </div>
        <div className="categories-grid">
          {categories.map(category => (
            <div
              key={category.id}
              className={`category-card ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id === 'all' ? null : category.id)}
            >
              <span className="category-icon">{category.icon}</span>
              <span className="category-name">{category.name}</span>
              <span className="category-count">{category.count} items</span>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Items Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2 className="section-title">Featured Items</h2>
          <Link to="/menu" className="view-all">View All</Link>
        </div>
        <div className="food-items-grid">
          {filteredFeaturedItems.map(item => (
            <div key={item._id} className="food-item-card">
              {item.discount && (
                <div className="discount-badge">{item.discount}% OFF</div>
              )}
              <img src={item.image} alt={item.name} className="food-item-image" />
              <div className="food-item-info">
                <div className="food-tags">
                  {item.tags?.map(tag => (
                    <span key={tag} className={`tag ${tag.toLowerCase()}`}>{tag}</span>
                  ))}
                </div>
                <h3>{item.name}</h3>
                <p className="food-description">{item.description}</p>
                <div className="food-item-meta">
                  <div className="rating">
                    <span>‚≠ê {item.rating}</span>
                    <span>‚è±Ô∏è {item.deliveryTime} min</span>
                  </div>
                  <span className="food-price">${item.price}</span>
                </div>
                <Link to={`/restaurant/${item.restaurantId}`} className="restaurant-link">
                  {item.restaurantName}
                </Link>
                <button className="add-to-cart-btn">Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">Popular Restaurants</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>
        <div className="restaurants-grid">
          {filteredRestaurants.map(restaurant => (
            <Link key={restaurant._id} to={`/restaurant/${restaurant._id}`} className="restaurant-card">
              <img src={restaurant.image} alt={restaurant.name} className="restaurant-image" />
              <div className="restaurant-info">
                <h3>{restaurant.name}</h3>
                <p className="cuisine">{restaurant.cuisine}</p>
                <div className="restaurant-meta">
                  <div className="delivery-info">
                    <span>‚≠ê {restaurant.rating}</span>
                    <span>‚è±Ô∏è {restaurant.deliveryTime} min</span>
                    <span>üí∞ {restaurant.priceRange}</span>
                  </div>
                  <div className="restaurant-status">
                    <span className={`status ${restaurant.isOpen ? 'open' : 'closed'}`}>
                      {restaurant.isOpen ? 'Open Now' : 'Closed'}
                    </span>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </section>

      {/* App Download Section */}
      <section className="app-download-section">
        <div className="app-content">
          <div className="app-info">
            <h2>Download Our App</h2>
            <p>Get exclusive offers and track your orders on the go</p>
            <div className="app-features">
              <div className="feature">
                <span className="feature-icon">üöö</span>
                <span>Fast Delivery</span>
              </div>
              <div className="feature">
                <span className="feature-icon">üí≥</span>
                <span>Easy Payments</span>
              </div>
              <div className="feature">
                <span className="feature-icon">üéÅ</span>
                <span>Exclusive Offers</span>
              </div>
            </div>
            <div className="app-buttons">
              <button className="app-store-btn">
                <span>Download on the</span>
                <span>App Store</span>
              </button>
              <button className="play-store-btn">
                <span>Get it on</span>
                <span>Google Play</span>
              </button>
            </div>
          </div>
          <div className="app-image">
            <img src="https://via.placeholder.com/400x800" alt="App Preview" />
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;

