import React, { useState, useEffect } from 'react';
import { FiShoppingCart, FiFilter, FiSearch } from 'react-icons/fi';
import { useCart } from '../context/CartContext.js';
import './OrderOnline.css';

const OrderOnline = () => {
  const [foodItems, setFoodItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeCategory, setActiveCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState('');
  const [notification, setNotification] = useState({ show: false, message: '', type: '' });
  const { addToCart } = useCart();

  // Categories for food items
  const categories = ['All', 'Appetizers', 'Main Course', 'Desserts', 'Beverages'];

  useEffect(() => {
    // In a real app, this would fetch from your API
    const fetchFoodItems = async () => {
      try {
        // Simulating API call with sample data
        const sampleFoodItems = [
          {
            _id: "1", // Changed id to _id to match CartContext expectations
            name: "Classic Margherita Pizza",
            description: "Fresh tomatoes, mozzarella, basil, and olive oil",
            price: 399,
            category: "Main Course",
            image: "https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?w=600&auto=format",
            isVeg: true,
            rating: 4.5,
            restaurant: "Pizza Haven",
            restaurantId: "1",
            preparation_time: "20 mins",
            calories: 650
          },
          {
            _id: "2",
            name: "Chicken Alfredo Pasta",
            description: "Creamy alfredo sauce with grilled chicken and parmesan",
            price: 449,
            category: "Main Course",
            image: "https://images.unsplash.com/photo-1645112411341-6c4fd023a3f1?w=600&auto=format",
            isVeg: false,
            rating: 4.3,
            restaurant: "Pasta Paradise",
            restaurantId: "2",
            preparation_time: "25 mins",
            calories: 850
          },
          {
            _id: "3",
            name: "Caesar Salad",
            description: "Crisp romaine lettuce, croutons, and caesar dressing",
            price: 249,
            category: "Appetizers",
            image: "https://images.unsplash.com/photo-1550304943-4f24f54ddde9?w=600&auto=format",
            isVeg: true,
            rating: 4.0,
            restaurant: "Fresh Greens",
            restaurantId: "3",
            preparation_time: "10 mins",
            calories: 320
          },
          {
            _id: "4",
            name: "Chocolate Lava Cake",
            description: "Warm chocolate cake with a molten center",
            price: 199,
            category: "Desserts",
            image: "https://images.unsplash.com/photo-1624353365286-3f8d62daad51?w=600&auto=format",
            isVeg: true,
            rating: 4.8,
            restaurant: "Sweet Delights",
            restaurantId: "4",
            preparation_time: "15 mins",
            calories: 410
          },
          {
            _id: "5",
            name: "Mango Smoothie",
            description: "Fresh mango blended with yogurt and honey",
            price: 149,
            category: "Beverages",
            image: "https://images.unsplash.com/photo-1623065422902-30a2d299bbe4?w=600&auto=format",
            isVeg: true,
            rating: 4.6,
            restaurant: "Juice Junction",
            restaurantId: "5",
            preparation_time: "5 mins",
            calories: 220
          },
          {
            _id: "6",
            name: "BBQ Burger",
            description: "Angus beef patty with BBQ sauce and crispy onions",
            price: 349,
            category: "Main Course",
            image: "https://images.unsplash.com/photo-1568901346375-23c9450c58cd?w=600&auto=format",
            isVeg: false,
            rating: 4.4,
            restaurant: "Burger Barn",
            restaurantId: "6",
            preparation_time: "18 mins",
            calories: 780
          },
          {
            _id: "7",
            name: "Garlic Bread",
            description: "Toasted bread with garlic butter and herbs",
            price: 129,
            category: "Appetizers",
            image: "https://images.unsplash.com/photo-1619535860434-cf9b2bca5c68?w=600&auto=format",
            isVeg: true,
            rating: 4.1,
            restaurant: "Italian Corner",
            restaurantId: "7",
            preparation_time: "8 mins",
            calories: 280
          },
          {
            _id: "8",
            name: "Tiramisu",
            description: "Italian coffee-flavored dessert",
            price: 229,
            category: "Desserts",
            image: "https://images.unsplash.com/photo-1571877227200-a0d98ea607e9?w=600&auto=format",
            isVeg: true,
            rating: 4.7,
            restaurant: "Dolce Vita",
            restaurantId: "8",
            preparation_time: "30 mins",
            calories: 380
          }
        ];

        setFoodItems(sampleFoodItems);
        setFilteredItems(sampleFoodItems);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch food items');
        setLoading(false);
      }
    };

    fetchFoodItems();
  }, []);

  useEffect(() => {
    // Filter items based on category and search term
    const filterItems = () => {
      let filtered = [...foodItems];

      // Filter by category
      if (activeCategory !== 'All') {
        filtered = filtered.filter(item => item.category === activeCategory);
      }

      // Filter by search term
      if (searchTerm) {
        filtered = filtered.filter(item => 
          item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          item.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      setFilteredItems(filtered);
    };

    filterItems();
  }, [activeCategory, searchTerm, foodItems]);

  const handleCategoryChange = (category) => {
    setActiveCategory(category);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleAddToCart = (item) => {
    addToCart(item);
    setNotification({
      show: true,
      message: `${item.name} added to cart!`,
      type: 'success'
    });

    // Hide notification after 3 seconds
    setTimeout(() => {
      setNotification({ show: false, message: '', type: '' });
    }, 3000);
  };

  if (loading) {
    return <div className="loading">Loading food items...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="order-online-container">
      {notification.show && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}

      <div className="order-online-header">
        <h1>Order Online</h1>
        <p>Delicious food delivered to your doorstep</p>
      </div>

      <div className="search-filter-container">
        <div className="search-bar">
          <FiSearch className="search-icon" />
          <input 
            type="text"
            placeholder="Search for dishes..." 
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
        <div className="filter-container">
          <button className="filter-button">
            <FiFilter /> Filter
          </button>
        </div>
      </div>

      <div className="category-tabs">
        {categories.map(category => (
          <button
            key={category}
            className={`category-tab ${activeCategory === category ? 'active' : ''}`}
            onClick={() => handleCategoryChange(category)}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="food-items-grid">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <div key={item._id} className="food-item-card">
              <div className="food-item-image-container">
                <img src={item.image} alt={item.name} className="food-item-image" />
                <div className={`veg-indicator ${item.isVeg ? 'veg' : 'non-veg'}`}>
                  <div className="indicator-dot"></div>
                </div>
              </div>
              <div className="food-item-info">
                <div className="food-header">
                  <h3>{item.name}</h3>
                  <div className="rating">
                    <span>★ {item.rating}</span>
                  </div>
                </div>
                <p className="food-description">{item.description}</p>
                <div className="food-item-details">
                  <span className="food-prep-time">{item.preparation_time}</span>
                  <span className="food-calories">{item.calories} cal</span>
                </div>
                <div className="food-item-footer">
                  <span className="food-price">₹{item.price}</span>
                  <button
                    className="add-to-cart-btn"
                    onClick={() => handleAddToCart(item)}
                  >
                    <FiShoppingCart /> Add
                  </button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="no-items">
            <p>No food items found matching your criteria.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderOnline;
