import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Footer from '../components/Footer';
import '../styles/Home.css';

// Mock data for testing
const mockCategories = [
  { id: 1, name: 'Pizza', icon: '🍕' },
  { id: 2, name: 'Burger', icon: '🍔' },
  { id: 3, name: 'Sushi', icon: '🍣' },
  { id: 4, name: 'Chinese', icon: '🥡' },
  { id: 5, name: 'Indian', icon: '🍛' },
  { id: 6, name: 'Mexican', icon: '🌮' },
  { id: 7, name: 'Italian', icon: '🍝' },
  { id: 8, name: 'Desserts', icon: '🍰' }
];

const mockRestaurants = [
  {
    _id: '1',
    name: 'Pizza Palace',
    cuisine: 'Italian',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.5',
    deliveryTime: '30-45 min',
    minOrder: '$10',
    deliveryFee: 'Free'
  },
  {
    _id: '2',
    name: 'Burger King',
    cuisine: 'American',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.2',
    deliveryTime: '20-30 min',
    minOrder: '$5',
    deliveryFee: '$2.99'
  },
  {
    _id: '3',
    name: 'Sushi Master',
    cuisine: 'Japanese',
    image: 'https://via.placeholder.com/300x200',
    rating: '4.8',
    deliveryTime: '25-35 min',
    minOrder: '$15',
    deliveryFee: 'Free'
  }
];

const mockFoodItems = [
  {
    _id: '1',
    name: 'Margherita Pizza',
    description: 'Classic pizza with tomato sauce, mozzarella, and basil',
    price: 12.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '1',
      name: 'Pizza Palace'
    },
    rating: 4.5,
    deliveryTime: '30 min'
  },
  {
    _id: '2',
    name: 'Cheeseburger',
    description: 'Juicy beef patty with cheese, lettuce, and special sauce',
    price: 8.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '2',
      name: 'Burger King'
    },
    rating: 4.2,
    deliveryTime: '20 min'
  },
  {
    _id: '3',
    name: 'California Roll',
    description: 'Fresh crab, avocado, and cucumber wrapped in seaweed',
    price: 10.99,
    image: 'https://via.placeholder.com/300x200',
    restaurant: {
      _id: '3',
      name: 'Sushi Master'
    },
    rating: 4.8,
    deliveryTime: '25 min'
  }
];

const Home = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    priceRange: 'all',
    rating: 'all',
    deliveryTime: 'all'
  });

  // Mock data for categories
  const categories = [
    { id: 'all', name: 'All', icon: '🍽️', count: 0 },
    { id: 'pizza', name: 'Pizza', icon: '🍕', count: 15 },
    { id: 'burger', name: 'Burgers', icon: '🍔', count: 12 },
    { id: 'sushi', name: 'Sushi', icon: '🍣', count: 8 },
    { id: 'pasta', name: 'Pasta', icon: '🍝', count: 10 },
    { id: 'salad', name: 'Salads', icon: '🥗', count: 6 }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [restaurantsRes, featuredRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants'),
          axios.get('http://localhost:5000/api/menu/featured')
        ]);
        setRestaurants(restaurantsRes.data);
        setFeaturedItems(featuredRes.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch data');
        setLoading(false);
        // Use mock data if API fails
        setRestaurants([
          {
            id: 1,
            name: 'Pizza Palace',
            image: 'https://via.placeholder.com/300x200',
            cuisine: 'Italian',
            rating: 4.5,
            deliveryTime: '30-45 min',
            isOpen: true
          },
          // Add more mock restaurants...
        ]);
        setFeaturedItems([
          {
            id: 1,
            name: 'Margherita Pizza',
            description: 'Classic tomato and mozzarella',
            price: 12.99,
            image: 'https://via.placeholder.com/300x200',
            restaurantId: 1,
            restaurantName: 'Pizza Palace',
            discount: '20% OFF',
            tags: ['veg']
          },
          // Add more mock items...
        ]);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    // Implement search functionality
    console.log('Searching for:', searchQuery);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddToCart = (item) => {
    // Implement add to cart functionality
    console.log('Adding to cart:', item);
  };

  const filteredItems = featuredItems.filter(item => {
    if (selectedCategory !== 'all' && !item.tags?.includes(selectedCategory)) {
      return false;
    }
    if (searchQuery && !item.name.toLowerCase().includes(searchQuery.toLowerCase())) {
      return false;
    }
    return true;
  });

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Delicious Food Delivered To You</h1>
          <p>Order your favorite meals from the best restaurants in town</p>
          <form onSubmit={handleSearch} className="search-bar">
            <input
              type="text"
              placeholder="Search for restaurants or dishes..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
          <div className="quick-filters">
            <button 
              className="filter-btn"
              onClick={() => setShowFilters(!showFilters)}
            >
              Filters
            </button>
            {showFilters && (
              <div className="filters-dropdown">
                <div className="filter-group">
                  <label>Price Range</label>
                  <select name="priceRange" value={filters.priceRange} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="cheap">Under $10</option>
                    <option value="medium">$10 - $20</option>
                    <option value="expensive">Over $20</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label>Rating</label>
                  <select name="rating" value={filters.rating} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="4">4+ Stars</option>
                    <option value="3">3+ Stars</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label>Delivery Time</label>
                  <select name="deliveryTime" value={filters.deliveryTime} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="fast">Under 30 min</option>
                    <option value="medium">30-45 min</option>
                    <option value="slow">Over 45 min</option>
                  </select>
                </div>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="section-header">
          <h2 className="section-title">Categories</h2>
        </div>
        <div className="categories-grid">
          {categories.map(category => (
            <div
              key={category.id}
              className={`category-card ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id)}
            >
              <span className="category-icon">{category.icon}</span>
              <span className="category-name">{category.name}</span>
              <span className="category-count">{category.count} items</span>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Items Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2 className="section-title">Featured Items</h2>
          <Link to="/menu" className="view-all">View All</Link>
        </div>
        <div className="food-items-grid">
          {filteredItems.map(item => (
            <div key={item.id} className="food-item-card">
              {item.discount && (
                <div className="discount-badge">{item.discount}</div>
              )}
              <img src={item.image} alt={item.name} className="food-item-image" />
              <div className="food-item-info">
                <div className="food-tags">
                  {item.tags?.map(tag => (
                    <span key={tag} className={`tag ${tag}`}>{tag}</span>
                  ))}
                </div>
                <h3>{item.name}</h3>
                <p className="food-description">{item.description}</p>
                <div className="food-item-meta">
                  <div className="rating">
                    <span>⭐ {item.rating}</span>
                  </div>
                  <span className="food-price">${item.price}</span>
                </div>
                <div className="restaurant-info">
                  <Link to={`/restaurant/${item.restaurantId}`} className="restaurant-link">
                    {item.restaurantName}
                  </Link>
                </div>
                <button
                  className="add-to-cart-btn"
                  onClick={() => handleAddToCart(item)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">Popular Restaurants</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>
        <div className="restaurants-grid">
          {restaurants.map(restaurant => (
            <Link
              key={restaurant.id}
              to={`/restaurant/${restaurant.id}`}
              className="restaurant-card"
            >
              <img src={restaurant.image} alt={restaurant.name} className="restaurant-image" />
              <div className="restaurant-info">
                <h3>{restaurant.name}</h3>
                <p className="cuisine">{restaurant.cuisine}</p>
                <div className="restaurant-meta">
                  <div className="delivery-info">
                    <span>⭐ {restaurant.rating}</span>
                    <span>⏱️ {restaurant.deliveryTime}</span>
                  </div>
                  <div className="restaurant-status">
                    <span className={`status ${restaurant.isOpen ? 'open' : 'closed'}`}>
                      {restaurant.isOpen ? 'Open' : 'Closed'}
                    </span>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
