import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FiClock, FiShoppingCart, FiStar, FiMapPin, FiSearch } from 'react-icons/fi';
import Footer from '../components/Footer.js';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();
  const [restaurants, setRestaurants] = useState([]);
  const [filteredRestaurants, setFilteredRestaurants] = useState([]);
  const [featuredItems, setFeaturedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categories] = useState(['All', 'Veg', 'Non-veg', 'Spicy', 'Healthy']);

  // Mock data for restaurants
  const mockRestaurants = [
    {
      id: 1,
      name: 'Tasty Bites',
      image: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8cmVzdGF1cmFudHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Indian',
      rating: 4.5,
      deliveryTime: '30-40 min',
      isOpen: true,
      discount: '20% OFF',
      location: 'Downtown'
    },
    {
      id: 2,
      name: 'Pizza Paradise',
      image: 'https://images.unsplash.com/photo-1555396273-367ea4eb4db5?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8cmVzdGF1cmFudHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Italian',
      rating: 4.8,
      deliveryTime: '25-35 min',
      isOpen: true,
      discount: '15% OFF',
      location: 'Midtown'
    },
    {
      id: 3,
      name: 'Sushi Master',
      image: 'https://images.unsplash.com/photo-1559339352-11d035aa65de?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTV8fHJlc3RhdXJhbnR8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Japanese',
      rating: 4.7,
      deliveryTime: '35-45 min',
      isOpen: false,
      location: 'Westside'
    },
    {
      id: 4,
      name: 'Green Garden',
      image: 'https://images.unsplash.com/photo-1552566626-52f8b828add9?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8OHx8cmVzdGF1cmFudHxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Vegetarian',
      rating: 4.3,
      deliveryTime: '20-30 min',
      isOpen: true,
      discount: '10% OFF',
      location: 'Eastside'
    },
    {
      id: 5,
      name: 'Burger Joint',
      image: 'https://images.unsplash.com/photo-1579027989536-b7b1f875659b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MTZ8fHJlc3RhdXJhbnR8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
      cuisine: 'American',
      rating: 4.6,
      deliveryTime: '15-25 min',
      isOpen: true,
      location: 'Downtown'
    },
    {
      id: 6,
      name: 'Taco House',
      image: 'https://images.unsplash.com/photo-1565749053788-617b022b8a49?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjJ8fHJlc3RhdXJhbnR8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
      cuisine: 'Mexican',
      rating: 4.4,
      deliveryTime: '25-40 min',
      isOpen: true,
      discount: '15% OFF',
      location: 'Uptown'
    }
  ];

  // Mock data for featured items
  const mockFeaturedItems = [
    {
      id: 1,
      name: 'Butter Chicken',
      description: 'Tender chicken cooked in rich tomato gravy with butter and cream',
      price: 299,
      image: 'https://images.unsplash.com/photo-1588166524941-3bf61a9c41db?ixlib=rb-4.0.3&auto=format&fit=crop&w=684&q=80',
      restaurant: 'Dev International',
      restaurantId: 1,
      rating: 4.5,
      tags: ['non-veg', 'spicy'],
      discount: '₹125 OFF ABOVE ₹349'
    },
    {
      id: 2,
      name: 'Paneer Butter Masala',
      description: 'Fresh cottage cheese cubes in creamy tomato gravy',
      price: 249,
      image: 'https://images.unsplash.com/photo-1631452180519-c014fe946bc7?ixlib=rb-4.0.3&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Apni Rasoi Family Dhaba',
      restaurantId: 2,
      rating: 4.3,
      tags: ['veg'],
      discount: '₹50 OFF ABOVE ₹349'
    },
    {
      id: 3,
      name: 'Chicken Biryani',
      description: 'Fragrant basmati rice cooked with tender chicken and aromatic spices',
      price: 279,
      image: 'https://images.unsplash.com/photo-1563379091339-03b21ab4a4f8?ixlib=rb-4.0.3&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Champaran Handi Restaurant',
      restaurantId: 3,
      rating: 4.4,
      tags: ['non-veg', 'spicy'],
      reviewCount: 156
    },
    {
      id: 4,
      name: 'Masala Dosa',
      description: 'Crispy rice crepe served with potato masala, sambar and chutneys',
      price: 149,
      image: 'https://images.unsplash.com/photo-1668236543090-82eba5ee5976?ixlib=rb-4.0.3&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Hotel Sai Nath & Sai Restaurant',
      restaurantId: 4,
      rating: 4.3,
      tags: ['veg', 'south-indian'],
      discount: '20% OFF'
    },
    {
      id: 5,
      name: 'Cheese Pizza',
      description: 'Fresh pizza with mozzarella cheese and Italian herbs',
      price: 199,
      image: 'https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-4.0.3&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Pizza Hut',
      restaurantId: 5,
      rating: 4.1,
      tags: ['veg', 'italian'],
      discount: '₹100 OFF ABOVE ₹399'
    },
    {
      id: 6,
      name: 'Veg Fried Rice',
      description: 'Wok-tossed rice with fresh vegetables and soy sauce',
      price: 179,
      image: 'https://images.unsplash.com/photo-1603133872878-684f208fb84b?ixlib=rb-4.0.3&auto=format&fit=crop&w=1170&q=80',
      restaurant: 'Accord International',
      restaurantId: 6,
      rating: 4.2,
      tags: ['veg', 'chinese'],
      discount: 'FREE DELIVERY'
    }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Use mock data directly instead of making API calls
        setRestaurants(mockRestaurants);
        setFilteredRestaurants(mockRestaurants);
        setFeaturedItems(mockFeaturedItems);
        setError(null);
        setLoading(false);

        // Uncomment this section when your backend is ready
        /*
        const [restaurantsRes, featuredItemsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/restaurants'),
          axios.get('http://localhost:5000/api/menu/featured')
        ]);

        setRestaurants(restaurantsRes.data);
        setFilteredRestaurants(restaurantsRes.data);
        setFeaturedItems(featuredItemsRes.data);
        setError(null);
        */
      } catch (err) {
        console.error('Error fetching data:', err);
        // Use mock data as fallback
        setRestaurants(mockRestaurants);
        setFilteredRestaurants(mockRestaurants);
        setFeaturedItems(mockFeaturedItems);
        setError('Using sample data for demonstration');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();

    if (searchQuery.trim() === '') {
      // If search is empty, reset to all restaurants
      setFilteredRestaurants(restaurants);
      return;
    }

    // Filter restaurants by name, cuisine, or location
    const filtered = restaurants.filter(restaurant =>
      restaurant.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      restaurant.cuisine.toLowerCase().includes(searchQuery.toLowerCase()) ||
      restaurant.location.toLowerCase().includes(searchQuery.toLowerCase())
    );

    if (filtered.length === 0) {
      // No results found - show message
      setError(`No restaurants found matching "${searchQuery}"`);
    } else {
      setError(null);
    }

    setFilteredRestaurants(filtered);
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    if (category) {
      const filteredItems = mockFeaturedItems.filter(item =>
        item.tags.includes(category.toLowerCase())
      );
      setFeaturedItems(filteredItems);
    } else {
      setFeaturedItems(mockFeaturedItems);
    }
  };

  const handleAddToCart = (item) => {
    // Save item to local storage cart
    const cart = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = cart.find(cartItem => cartItem.id === item.id);

    if (existingItem) {
      existingItem.quantity = (existingItem.quantity || 1) + 1;
      localStorage.setItem('cart', JSON.stringify(cart));
    } else {
      cart.push({ ...item, quantity: 1 });
      localStorage.setItem('cart', JSON.stringify(cart));
    }

    alert('Item added to cart!');
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Delicious Food Delivered To Your Doorstep</h1>
          <p>Order from your favorite restaurants with just a few clicks</p>
          <form className="search-bar" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for restaurants or cuisine..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button type="submit">
              <FiSearch className="search-icon" /> Search
            </button>
          </form>
        </div>
      </section>

      {/* Categories */}
      <section className="categories-section">
        <div className="container">
          <div className="category-tabs">
            {categories.map((category, index) => (
              <button
                key={index}
                className={`category-tab ${selectedCategory === category.toLowerCase() ? 'active' : ''}`}
                onClick={() => handleCategorySelect(category === 'All' ? null : category.toLowerCase())}
              >
                {category}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Items Section */}
      <section className="featured-section">
        <div className="section-header">
          <h2 className="section-title">Featured Items</h2>
          <Link to="/menu" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
        ) : error && !searchQuery ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => window.location.reload()}>Try Again</button>
          </div>
        ) : (
          <div className="food-items-grid">
            {featuredItems.map((item) => (
              <div key={item.id} className="food-item-card">
                {item.discount && (
                  <div className="discount-badge">{item.discount}</div>
                )}
                <div className="food-image-container">
                  <img src={item.image} alt={item.name} className="food-item-image" />
                </div>
                <div className="food-item-info">
                  <div className="food-tags">
                    {item.tags.map((tag, index) => (
                      <span key={index} className={`tag ${tag}`}>{tag}</span>
                    ))}
                  </div>
                  <h3>{item.name}</h3>
                  <p className="food-description">{item.description}</p>
                  <div className="food-item-meta">
                    <div className="rating">
                      <span><FiStar /> {item.rating} {item.reviewCount && <small>({item.reviewCount})</small>}</span>
                      <span className="restaurant-link">
                        <Link to={`/restaurant/${item.restaurantId}`}>
                          {item.restaurant}
                        </Link>
                      </span>
                    </div>
                    <span className="food-price">₹{item.price}</span>
                  </div>
                  <button
                    className="add-to-cart-btn"
                    onClick={() => handleAddToCart(item)}
                  >
                    <FiShoppingCart /> Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Restaurants Section */}
      <section className="restaurants-section">
        <div className="section-header">
          <h2 className="section-title">Popular Restaurants</h2>
          <Link to="/restaurants" className="view-all">View All</Link>
        </div>

        {loading ? (
          <div className="loading-spinner">
            <div className="spinner"></div>
          </div>
        ) : error && searchQuery ? (
          <div className="error-message">
            <p>{error}</p>
            <button onClick={() => {
              setSearchQuery('');
              setFilteredRestaurants(restaurants);
              setError(null);
            }}>Clear Search</button>
          </div>
        ) : (
          <div className="restaurants-grid">
            {filteredRestaurants.length > 0 ? (
              filteredRestaurants.map((restaurant) => (
                <Link
                  key={restaurant.id}
                  to={`/restaurant/${restaurant.id}`}
                  className="restaurant-card"
                >
                  <div className="restaurant-image-container">
                    <img
                      src={restaurant.image}
                      alt={restaurant.name}
                      className="restaurant-image"
                    />
                    {restaurant.discount && (
                      <div className="discount-badge">{restaurant.discount}</div>
                    )}
                  </div>
                  <div className="restaurant-info">
                    <h3>{restaurant.name}</h3>
                    <p className="cuisine">{restaurant.cuisine}</p>
                    <div className="restaurant-meta">
                      <span className="rating"><FiStar /> {restaurant.rating}</span>
                      <span className="delivery-info">
                        <FiClock /> {restaurant.deliveryTime}
                      </span>
                      <span className="location">
                        <FiMapPin /> {restaurant.location}
                      </span>
                    </div>
                    <div className="restaurant-status">
                      <span className={`status ${restaurant.isOpen ? 'open' : 'closed'}`}>
                        {restaurant.isOpen ? 'Open Now' : 'Closed'}
                      </span>
                    </div>
                  </div>
                </Link>
              ))
            ) : (
              <div className="no-results">
                <h3>No restaurants found</h3>
                <p>Try changing your search query</p>
                <button className="reset-search" onClick={() => {
                  setSearchQuery('');
                  setFilteredRestaurants(restaurants);
                }}>Show All Restaurants</button>
              </div>
            )}
          </div>
        )}
      </section>

      <Footer />
    </div>
  );
};

export default Home;
