import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { FiHeart } from "react-icons/fi";
import RestaurantCard from "../components/RestaurantCard";
import "../styles/Favorites.css";

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    const fetchFavorites = () => {
      setLoading(true);
      
      try {
        // Get favorites from localStorage
        const favoritesString = localStorage.getItem("favorites");
        const favoritesData = favoritesString ? JSON.parse(favoritesString) : [];
        
        // Add short delay to simulate loading for better UX
        setTimeout(() => {
          setFavorites(favoritesData);
          setLoading(false);
        }, 500);
      } catch (err) {
        console.error("Error fetching favorites:", err);
        setError("Failed to load favorites. Please try again later.");
        setLoading(false);
      }
    };
    
    fetchFavorites();
  }, []);
  
  const handleRemoveFavorite = (id) => {
    // Remove from state
    const updatedFavorites = favorites.filter(restaurant => restaurant.id !== id);
    setFavorites(updatedFavorites);
    
    // Update localStorage
    localStorage.setItem("favorites", JSON.stringify(updatedFavorites));
  };
  
  if (loading) {
    return (
      <div className="favorites-container">
        <div className="favorites-loading">
          <FiHeart className="loading-icon pulse" />
          <p>Loading your favorites...</p>
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="favorites-container">
        <div className="favorites-error">
          <p>{error}</p>
        </div>
      </div>
    );
  }
  
  return (
    <div className="favorites-container">
      <div className="favorites-header">
        <h1>Your Favorites</h1>
        <p>Restaurants you've loved</p>
      </div>
      
      {favorites.length === 0 ? (
        <div className="no-favorites">
          <FiHeart className="empty-heart" />
          <h2>No favorites yet</h2>
          <p>You haven't added any restaurants to your favorites.</p>
          <div className="explore-action">
            <Link to="/" className="explore-button">Explore Restaurants</Link>
          </div>
        </div>
      ) : (
        <div className="favorites-grid">
          {favorites.map(restaurant => (
            <RestaurantCard
              key={restaurant.id}
              restaurant={restaurant}
              isFavorite={true}
              onRemoveFavorite={handleRemoveFavorite}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Favorites;
